INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('c3b7ff40-d7f7-4b5f-8daa-48cc2b99ca0e', '2024-04-12T15:29:21.402Z', 'LongLLaMa', 'https://github.com/CStanKonrad/long_llama', 'images/longllama.webp', 'images/longllama-icon.svg', '{"what":"LongLLaMA is a large language model that can handle long contexts, fine-tuned with the Focused Transformer (FoT) method and based on OpenLLaMA.","who":"LongLLaMA is for developers and researchers in natural language processing tasks who need a powerful language model for processing and understanding long contexts.","best_features":["Capable of handling long contexts up to 256k tokens","Utilizes the FoT method for enhanced focus on specific areas of input text","Can be leveraged in various applications such as natural language processing, text generation, and sentiment analysis"]}', 'LongLLaMA is a large language model that has been fine-tuned with the Focused Transformer (FoT) method, allowing it to effectively process and understand long contexts. It is based on OpenLLaMA and hosted on GitHub as a public repository created by CStanKonrad.', NULL, '["Natural Language Processing","Text Generation","Sentiment Analysis"]');
