INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('575b00d2-b3ca-4be2-b2cd-4fb0db6e60bf', '2024-04-12T15:29:21.431Z', 'MapDeduce', 'https://mapdeduce.com/', 'images/mapdeduce.webp', 'images/mapdeduce-icon.webp', '{"what":"MapDeduce is an AI tool designed to help users understand complex documents by analyzing content from PDFs or images. It offers summarization and language analysis capabilities to identify red-flag terms in contracts and soon will provide comparative insights on similar documents.","who":"MapDeduce is for professionals dealing with large volumes of complex documents, such as legal, financial, or business professionals, as well as students and researchers who need to quickly extract key information from research papers, textbooks, and study guides.","best_features":["Efficiently process and summarize documents in any language","Spot potential red-flag terms in contracts for business critical analysis","Quickly retrieve relevant information from lengthy texts, saving valuable time"]}', 'MapDeduce is an AI tool that helps users understand complex documents by analyzing content from PDFs or images. It offers summarization and language analysis capabilities to identify red-flag terms in contracts and soon will provide comparative insights on similar documents.', NULL, '["Document Analysis","Contract Analysis","Text Summarization"]');
