INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('16ccf8d3-1c07-4da2-8efe-93b7e96b7fab', '2024-04-12T15:29:21.454Z', 'Mimrr', 'https://www.mimrr.com/', 'images/mimrr.webp', 'images/mimrr-icon.webp', '{"what":"Mimrr is a tool designed to automate code documentation, allowing developers to focus on building products rather than spending time on manual documentation tasks. It supports multiple programming languages and offers intuitive code commenting functionality.","who":"Mimrr is ideal for developers and software development teams who want to streamline their code documentation process, onboard new developers more efficiently, and provide richer context for maintenance and debugging.","best_features":["Effortless documentation generation for codebases, updating automatically with code changes.","Support for multiple programming languages with plans for additional language support in the future.","Intuitive code commenting functionality with a single-click feature in the IDE for detailed descriptions."],"pricing":[{"plan":"Free","price":0,"billing_cycle":"N/A","original_price":0,"discount":"N/A","features":["Automated code documentation generation","Support for multiple languages","Intuitive code commenting functionality"]}]}', 'Mimrr is a tool that automates code documentation, allowing developers to effortlessly generate and update their code documentation site whenever changes are made to the code. It supports Typescript, Javascript, and C#, with plans to add more languages in the future. Mimrr''s key features include intuitive code commenting, multi-project support, pipeline integration, and public/private hosting options.', NULL, '["Code Documentation","Automation","Software Development"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('a62f0047-1274-4513-bf36-f60f6688c80d', '2024-04-12T15:29:21.454Z', 'Mind Video', 'https://mind-video.com/', 'images/mind-video.webp', 'images/mind-video-icon.webp', '{"what":"Mind-Video is an AI tool that reconstructs high-quality videos from brain activity captured through fMRI data, offering a groundbreaking approach to visual reconstruction.","who":"This product is designed for researchers, neuroscientists, AI enthusiasts, and anyone interested in the intersection of technology and brain imaging.","best_features":["Advanced two-module pipeline for image and video brain decoding","Flexible and adaptable pipeline for fMRI data reconstruction","Progressive learning scheme for improved semantic accuracy in video generation"],"pricing":""}', 'Mind-Video is an innovative AI tool that reconstructs high-quality videos from brain activity captured through fMRI data. It employs a two-module pipeline to bridge the gap between image and video brain decoding, offering superior semantic accuracy and motion dynamics. The tool''s progressive learning scheme and attention analysis of the transformers decoding fMRI data make it a cutting-edge solution for visual reconstruction.', NULL, '["Brain Imaging","Video Reconstruction"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('899311f1-2fff-4417-86f5-4a9556681620', '2024-04-12T15:29:21.454Z', 'MindBranches', 'https://teamonics.com/mindbranches', 'images/mindbranches.webp', 'images/mindbranches-icon', '{"what":"MindBranches is an AI tool developed by Teamonics to simplify the comprehension of large volumes of data and complex concepts using GPT-4 AI technology.","who":"MindBranches is designed for individuals who need assistance in understanding and utilizing vast amounts of information quickly and efficiently.","best_features":["Breaks down outputs from GPT-4 AI into smaller, interconnected segments for easier understanding.","Facilitates quicker learning and application of knowledge.","Acts as an intermediator between users and high volumes of data, simplifying tasks."],"pricing":""}', 'MindBranches is an AI tool developed by Teamonics to assist individuals in comprehending sizable volumes of data and complex concepts in a simplified manner. Leveraging GPT-4 AI technology, MindBranches breaks down outputs into smaller segments, making it easier for users to understand and utilize information. It enables quicker learning and application of knowledge by simplifying tasks and acting as an intermediator between users and high volumes of data.', NULL, '["Education","Data Analysis","Artificial Intelligence"]');
