INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('c8b3763f-6973-4956-a277-5362b7dfe8ac', '2024-04-12T15:29:21.458Z', 'Minerva AI', 'https://academicid.net/minerva-ai', 'images/minerva-ai.webp', 'images/minerva-ai-icon.webp', '{"what":"Minerva AI is an AI-powered research assistant designed for academics and researchers. It utilizes advanced machine learning and natural language processing technologies to provide accurate and insightful responses to research questions.","who":"Minerva AI is for academics and researchers who require assistance in conducting literature reviews, accessing the latest research, and obtaining up-to-date information for their work.","best_features":["Advanced machine learning and natural language processing technologies","Ability to undertake literature reviews of up to 200 million academic papers","Integration with other advanced data analytics tools"],"pricing":[]}', 'Minerva is an AI-powered research assistant tailored for academics and researchers. It offers accurate and insightful responses to research questions by leveraging advanced machine learning and natural language processing technologies. With the ability to conduct literature reviews of up to 200 million academic papers and integrate with other data analytics tools, Minerva ensures users have access to the most up-to-date information for their research.', NULL, '["AI Assistance","Data Analysis","AI Services"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('a936649d-f585-48ff-96f5-400bf4fffc0d', '2024-04-12T15:29:21.458Z', 'Minigpt-4', 'https://minigpt-4.github.io/', 'images/minigpt-4.webp', 'images/minigpt-4-icon.webp', '{"what":"MiniGPT-4 is an AI model that focuses on enhancing vision-language understanding using advanced large language models. It aligns a frozen visual encoder with a frozen LLM called Vicuna using one projection layer.","who":"MiniGPT-4 is designed for individuals and businesses looking to improve vision-language understanding, generate detailed image descriptions, create websites from hand-written drafts, write stories and poems inspired by images, provide solutions to image-based problems, and teach cooking based on food photos.","best_features":["Advanced multi-modal generation capabilities similar to GPT-4","Efficient training with approximately 5 million aligned image-text pairs","Capabilities include detailed image description generation, website creation, story and poem writing, problem-solving, and cooking instructions"]}', 'MiniGPT-4 is an AI model that enhances vision-language understanding by aligning a frozen visual encoder with a frozen large language model called Vicuna using a single projection layer. It exhibits capabilities similar to GPT-4, such as generating detailed image descriptions, creating websites from hand-written drafts, writing stories and poems inspired by images, providing solutions to image-based problems, and teaching cooking based on food photos. The model is highly computationally efficient, requiring approximately 5 million aligned image-text pairs for training the projection layer.', NULL, '["Art Generation","AI Assistance","Image Generation"]');
