INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('40f9ee76-3751-4148-8961-dc2d9e339e17', '2024-04-12T15:29:21.465Z', 'Moments', 'https://scheduledapp.com/moments/', 'images/moments.webp', 'images/moments-icon.webp', '{"what":"Moments is a Personal Relationship Manager (PRM) tool designed to help users deepen their personal and professional relationships by being thoughtful at the right moments.","who":"Moments is for individuals who want to organize, prioritize, and strengthen their social connections effortlessly, both personally and professionally.","best_features":["Social To-Dos: Preview upcoming events and reminders to reduce cognitive load and stress.","Social Circles: Assort contacts into different groups based on importance levels.","AI Composer: Compose personalized messages and emails tailored to each contact''s unique relationship and interests."],"pricing":[]}', 'Moments is a Personal Relationship Manager (PRM) tool that helps users deepen their personal and professional relationships by assisting them in being thoughtful at the right moments. It allows users to organize, prioritize, and strengthen their social connections effortlessly, with features such as managing social to-do lists, organizing contacts into different groups, creating events and reminders, and utilizing an AI Composer for personalized messages. Moments also offers resources to improve social skills and focuses on simplifying relationship management and enhancing communication.', NULL, '["Relationships","AI Tools","Communication"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('7f386f22-e855-4162-acac-5470b321dc96', '2024-04-12T15:29:21.465Z', 'MonAI', 'https://www.get-monai.app/', 'images/monai.webp', 'images/monai-icon.webp', '{"what":"MonAi is an AI-powered expense tracking tool that allows users to enter expenses through voice messages, making the process quick and effortless.","who":"MonAi is designed for individuals who want to simplify their expense tracking process and save time on manual data entry.","best_features":["Effortless expense entry through voice messages","Secure storage in user''s private iCloud account","Sharing and collaboration functionalities for easy expense management"],"pricing":[]}', 'MonAi is an AI-powered expense tracking tool that enables users to input expenses through voice messages. The tool automatically splits the input into a short description, amount, and category, eliminating the need for manual data entry. Expenses are securely stored in the user''s private iCloud account, with sharing and collaboration features available. MonAi does not require any login, providing a streamlined and user-friendly experience for managing expenses.', NULL, '["Expense Tracking","AI-Powered Tool","Voice Recognition"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('2783bc75-5a7d-42c3-9ef8-57706ed81aed', '2024-04-12T15:29:21.466Z', 'Moncelo', 'https://www.moncelo.com/', 'images/moncelo.webp', 'images/moncelo-icon.webp', '{"what":"Moncelo is an AI-powered personal project management tool designed to help users manage their projects more efficiently by leveraging AI technology.","who":"Moncelo is for individuals and teams looking to streamline their project management process and improve efficiency in their work.","best_features":["AI-generated tasks to help users stay on track and unlock their creativity.","Project overview dashboard for a comprehensive view of all projects and tasks.","Two pricing options - monthly subscription or one-time lifetime payment - to suit different user preferences."],"pricing":[{"plan":"Monthly","price":5,"billing_cycle":"per month","original_price":0,"discount":"7 Day Free Trial","features":["Unlimited projects","AI generated tasks","Dashboard for project overview","Cancel anytime"]},{"plan":"Lifetime","price":60,"billing_cycle":"forever","original_price":0,"discount":"popular","features":["Unlimited projects","AI generated tasks","Dashboard for project overview","Pay once, keep it forever"]}]}', 'Moncelo is an AI-powered personal project management tool that aims to improve project management efficiency by providing AI-generated tasks, project overview dashboard, and two pricing options - monthly subscription or one-time lifetime payment. Users can track their progress, unlock creativity, and manage unlimited projects with the flexibility to cancel anytime. The tool offers a 7-day free trial period for users to explore its capabilities before committing to a subscription.', NULL, '["AI Solutions","Productivity Tools"]');
