INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('f02d5877-efe3-462f-8093-b37a0f458e56', '2024-04-12T15:29:21.466Z', 'Monic.ai', 'https://beta.monic.ai/', 'images/monic-ai.webp', 'images/monic-ai-icon.webp', '{"what":"Monic.ai is a comprehensive AI-powered study platform designed to transform the way students learn by providing personalized study materials such as quizzes, flashcards, and summaries.","who":"Monic.ai is for students at various academic levels, educators, and professionals looking to upskill. It is designed to support a wide range of subjects and adapt to various learning styles.","best_features":["AI-powered study platform with tools for creating personalized study materials","Summarizer tool to transform learning materials into summaries","Quiz maker for creating interactive quizzes"],"pricing":[{"plan":"Monic Basic","price":0,"billing_cycle":"Free forever","original_price":0,"discount":"","features":["2 study spaces","2500 tokens","Up to 20 AI daily chats"]},{"plan":"Monic Pro Semesterly","price":42,"billing_cycle":"6 months","original_price":60,"discount":"at a discount","features":["Unlimited study spaces","Unlimited tokens","Unlimited AI chats"]},{"plan":"Monic Pro Monthly","price":10,"billing_cycle":"per month","original_price":10,"discount":"","features":["Unlimited study spaces","Unlimited tokens","Unlimited AI chats"]}]}', 'Monic.ai is an AI-powered learning tool that aims to make your learning journey interactive, effective, and fun. It provides support for over 60 languages and offers tools for creating assessments, flashcards, summaries, and study guides in seconds. Monic.ai utilizes artificial intelligence models to analyze and generate customized resources based on uploaded materials, making learning more efficient and engaging.', NULL, '["AI-powered Learning","Study Tools","Personalized Learning"]');
