INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('5607e1a8-fd62-460d-a66d-0fddff5f3f06', '2024-04-12T15:29:21.466Z', 'Moning', 'https://moning.co', 'images/moning.webp', 'images/moning-icon.webp', '{"what":"Moning is a wealth management tool that combines human expertise with AI to provide insights and analysis for better investment decisions. It offers a comprehensive view of portfolios, stocks, ETFs, funds, and cryptocurrencies, emphasizing the need for users to verify information from multiple sources due to the inherent risks in financial investments.","who":"Moning is designed for individuals who want to track and manage their wealth, make better investment decisions, avoid costly mistakes, and increase their investment performance. It is suitable for both experienced investors and beginners looking to improve their knowledge of the stock market.","best_features":["Comprehensive view of portfolios, stocks, ETFs, funds, and cryptocurrencies","Exclusive AI Analysis feature for personalized insights","Dividend and Growth Safety Scores to help users make confident investment decisions"],"pricing":[]}', 'Moning is a wealth management tool that offers a comprehensive view of portfolios, stocks, ETFs, funds, and cryptocurrencies. It combines human expertise with AI to provide insights and analysis for better investment decisions. Users can anticipate dividends, assess stock safety scores, and access screening tools to identify investment opportunities. However, Moning is purely educational and doesn''t manage funds or offer investment advice, emphasizing the need for users to verify information from multiple sources due to the inherent risks in financial investments.', NULL, '["Wealth Management","Investment Analysis","Financial Education"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('a6305324-cd6b-4fae-8624-9a72869e0241', '2024-04-12T15:29:21.467Z', 'MonitUp Time Tracking', 'https://timetracking.monitup.com/', 'images/monitup-time-tracking.webp', 'images/monitup-time-tracking-icon.webp', '{"what":"MonitUp Time Tracking is a productivity tool that helps users track their computer activity, monitor apps and websites, and receive AI-generated suggestions to improve productivity.","who":"MonitUp Time Tracking is designed for individuals looking to enhance their personal productivity, remote employees seeking performance appraisal tools, and freelancers managing project-based work.","best_features":["AI-powered suggestions for enhanced productivity","Powerful screenshot feature for time tracking and monitoring","Customizable application categorization for accurate reports"],"pricing":[{"plan":"FREELANCER","price":6,"billing_cycle":"user / month","original_price":6,"discount":"","features":["AI Suggestions","Open/Close Time List","App and URL Tracking"]},{"plan":"PREMIUM","price":9,"billing_cycle":"user / month","original_price":9,"discount":"","features":["AI Suggestions","Open/Close Time List","Setting Working Hours"]}]}', 'MonitUp Time Tracking is a productivity tool that allows users to track computer activity, monitor apps and websites, and receive AI-generated suggestions to improve productivity. It offers a user dashboard, powerful screenshot feature, and customizable application categorization for accurate reports.', NULL, '["AI Assistance","Time Tracking","Performance Appraisal"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('ed19746a-97e4-4683-8fe1-15979bd24a99', '2024-04-12T15:29:21.467Z', 'MonkeeMath', 'https://monkeemath.com/', 'images/monkeemath.webp', 'images/monkeemath-icon.webp', '{"what":"MonkeeMath is a website that utilizes artificial intelligence to analyze sentiment in stock market-related comments from platforms like Reddit and Stocktwits, providing users with insights to make informed investment decisions.","who":"MonkeeMath is designed for investors, traders, and individuals interested in tracking market sentiment and making data-driven investment choices.","best_features":["AI-powered sentiment analysis of stock market comments from Reddit and Stocktwits","Prediction mini game feature for users to make their own stock predictions","User leaderboard to showcase the accuracy of predictions"],"pricing":[]}', 'MonkeeMath is a platform that leverages AI technology to track sentiment in stock market comments from Reddit and Stocktwits, offering users insights into market sentiment and the likelihood of stock price movements. Users can view sentiment charts, raw comments, and participate in a prediction mini game to test their forecasting skills.', NULL, '["Investment","AI Tools","Data Analysis"]');
