INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('2ce5d046-e964-4da3-9e22-6ad81637fa55', '2024-04-12T15:29:21.516Z', 'Observo', 'https://www.observo.ai/', 'images/observo.webp', 'images/observo-icon.webp', '{"what":"Observo is an AI-powered Observability Pipeline designed for Security, IT, and DevOps data, offering dynamic and continually learning solutions.","who":"Observo is designed for organizations looking to reduce security and observability costs while improving incident resolution times.","best_features":["Data Optimization & Reduction: Cut log volumes by 80% and reduce the total cost of observability by 50% or more.","Anomaly Detection: Lower Mean Time to Resolution (MTTR) of incidents by 40% or more.","Smart Routing: Avoid vendor lock-in by routing data where it has the most value."],"pricing":[]}', 'Observo is an AI-powered Observability Pipeline that goes beyond static, rules-based pipelines by offering dynamic, continually learning solutions for Security, IT, and DevOps data. It provides features such as data optimization and reduction, smart routing, anomaly detection, a searchable low-cost data lake, data enrichment, and sensitive data discovery.', NULL, '["AI Tools","Machine Learning","Artificial Intelligence"]');
