INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('dcc1cba0-4fde-45fc-9acb-6ee42c607a5d', '2024-04-12T15:29:21.526Z', 'Op app', 'https://opapp.io/', 'images/op-app.webp', 'images/op-app-icon.svg', '{"what":"Op is a data analysis tool that combines spreadsheets, Jupyter notebooks, and AI-chat to simplify the data analysis process by generating code based on user questions.","who":"Op is designed for data analysts, researchers, and professionals who need to analyze data efficiently without struggling with Python pandas.","best_features":["AI-generated code based on user questions for quick data analysis","Syncing visual tables with data frames in the code for better data visibility","Free trial with no credit card required to experience the tool before committing to a paid plan"],"pricing":[]}', 'Op is a data analysis tool that provides an easy and efficient way to analyze data using code. It simplifies the data analysis process by combining spreadsheets, Jupyter notebooks, and AI-chat to generate context-relevant code based on user questions. The tool aims to save users time by eliminating the need to search for Python error solutions online and enhances data understanding through visual tables synced with data frames.', NULL, '["AI Tools","Coding Assistance","Productivity Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('c0289538-b41b-41cd-9505-f5c997143fb6', '2024-04-12T15:29:21.527Z', 'Open-Audio TTS', 'https://www.openaudio.ai/', 'images/open-audio-tts.webp', 'images/open-audio-tts-icon.webp', '{"what":"Open-Audio TTS is a Text-to-Speech tool developed utilizing OpenAI''s technology. It converts input text into verbal speech with customizable voice types and speed control.","who":"This tool is for users needing to transform written content into audio format quickly and effectively. It can be used for creating podcast content, generating audiobooks, assisting visually impaired individuals, and more.","best_features":["Customizable voice types like Alloy, Echo, Fable, etc.","Control over the speed of speech for desired audio effect.","Flexibility to convert any type of text into audio format."],"pricing":[{"plan":"Free","price":0,"billing_cycle":"N/A","original_price":0,"discount":"N/A","features":["Selectable voice types","Speed control","Text-to-audio conversion"]}]}', 'Open-Audio TTS is a Text-to-Speech tool powered by OpenAI''s technology, allowing users to convert input text into verbal speech with customizable voice types and speed control. It is useful for various applications such as creating podcast content, generating audiobooks, and assisting visually impaired individuals. Users can freely generate an API Key on Github to use this tool.', NULL, '["Text-to-Speech","AI Solutions"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('b76994a1-8d6c-4bba-95e2-24cf04d2c885', '2024-04-12T15:29:21.527Z', 'Open Interpreter', 'https://openinterpreter.com/', 'images/open-interpreter.webp', 'images/open-interpreter-icon.webp', '{"what":"The Open Interpreter Project is a free and open-source code interpreter designed for running code on computers to accomplish various tasks. It allows Limited Language Models (LLMs) to execute code through a web browser.","who":"This product is for developers, programmers, and individuals looking for a versatile and user-friendly platform to run code efficiently.","best_features":["Free and open-source code interpreter","Allows Limited Language Models to execute code","User-friendly platform with documentation and support"],"pricing":[]}', 'The Open Interpreter Project is a free and open-source code interpreter that enables users to run code on computers to perform specific functions. It offers a new approach to utilizing computers by providing a platform for executing code efficiently. Users can view, modify, and contribute to the development of the tool according to their needs. The project is hosted on GitHub and provides additional resources such as documentation and support.', NULL, '["Open-source","Code Interpreter","Web Development"]');
