INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('5b09a2ba-0ea4-494c-ba66-bcf549eb8bb3', '2024-04-12T15:29:21.527Z', 'OpenArt', 'https://openart.ai/home', 'images/openart.webp', 'images/openart-icon.webp', '{"what":"OpenArt is an AI art generator that provides powerful tools for generating and editing images, especially artist assets, with ease.","who":"OpenArt is for artists, fashion designers, marketers, writers, and anyone who wants to create or edit AI images effortlessly.","best_features":["User-friendly Magic Brush for easy image editing","Ability to start with a prompt or use any AI image app","Wide range of creative tools for unlocking new potentials"],"pricing":[]}', 'OpenArt is an AI art generator that simplifies image creation by offering a variety of tools to generate and edit images effortlessly. Whether you''re an artist looking to unlock new creative potential or a marketer aiming to elevate your brand, OpenArt provides the necessary features to bring your ideas to life. With options to start with a prompt or use AI image apps, along with a user-friendly Magic Brush for polishing images, OpenArt makes the image editing process a breeze.', NULL, '["AI Tools","Image Editing","Creative Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('91055de7-acc8-4759-b841-db578b40bc26', '2024-04-12T15:29:21.528Z', 'OpenBuckets', 'https://openbuckets.io/', 'images/openbuckets.webp', 'images/openbuckets-icon.webp', '{"what":"OpenBuckets is an online platform that helps users easily find open buckets in cloud storage systems through a simple search, utilizing AI-powered algorithms to enhance efficiency and effectiveness in uncovering vulnerable documents.","who":"OpenBuckets is designed for security experts, researchers, and individuals interested in identifying exposed data. It is particularly valuable for organizations looking to prevent data exposure due to misconfigured buckets.","best_features":["AI-powered algorithms for quick identification of exposed buckets","Targeted file type searches within buckets for specific file identification","Support for multiple cloud providers and daily database updates for continuous monitoring"],"pricing":[]}', 'OpenBuckets is a comprehensive online platform that enables users to discover and secure open buckets in cloud storage systems. It offers AI-powered algorithms for quick identification of exposed buckets, targeted file type searches, filtering options, support for multiple cloud providers, daily database updates, RESTful APIs for developers, and secret scanning capabilities for enhanced cybersecurity.', NULL, '["Cloud Storage","Cybersecurity","AI-powered Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('8dc3d05c-cf09-49ff-854a-f3a9f63f03dc', '2024-04-12T15:29:21.528Z', 'OpenBuddy', 'https://openbuddy.ai/', 'images/openbuddy.webp', 'images/openbuddy-icon.webp', '{"what":"OpenBuddy.ai is an advanced multilingual chatbot tool designed to facilitate seamless conversations in multiple languages, including English, Chinese, and more.","who":"OpenBuddy.ai is for anyone seeking an efficient and comprehensive AI chatbot solution that seamlessly bridges language barriers and provides valuable insights across multiple domains.","best_features":["Advanced Multilingual Support: Seamless conversations in multiple languages.","Falcon/LLaMA based Architecture: Built on top of advanced models for enhanced performance.","Future Development Plans: Continuous improvements and new features like support for more languages and multimodal capabilities."]}', 'OpenBuddy.ai is an advanced multilingual chatbot tool designed to facilitate seamless conversations in multiple languages, including English, Chinese, and more. Its conversational AI capabilities allow users to engage in smooth and interactive interactions. OpenBuddy is built on the Falcon model from Tii and the LLaMA model from Facebook, enabling enhanced performance in handling complex conversational tasks. With its extensive language support, OpenBuddy facilitates translations and provides insightful information in various languages.', NULL, '["Chat Solutions","Translation","Multilingual Support"]');
