INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('fb27d8b9-a28f-4064-9b8b-90201122bf63', '2024-04-12T15:29:21.531Z', 'Opinly.ai', 'https://www.opinly.ai/', 'images/opinly-ai.webp', 'images/opinly-ai-icon', '{"what":"Opinly.ai is an automated tool for competitor analysis, offering real-time tracking of pricing and feature developments without needing data science skills. It simplifies market insights and strategic decision-making for individuals and companies.","who":"Opinly.ai is for individuals and companies looking to gain strategic insights and save time on complex data analysis. It is particularly beneficial for small to medium-sized enterprises.","best_features":["Real-time price monitoring for competitors","Email reports with key insights delivered directly to your inbox","Easy access to key price stats and metrics by simply entering a URL"]}', 'Opinly.ai is an automated tool for competitor analysis that simplifies market insights and strategic decision-making. It offers real-time tracking of pricing and feature developments, making it easy to gather valuable insights into market dynamics. With features like real-time price monitoring and email reports, Opinly.ai helps users stay ahead in the dynamic world of online business.', NULL, '["Data Analysis","Market Intelligence","Competitive Analysis"]');
