INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('207f55ca-ebb1-4591-b727-7db9afe65504', '2024-04-12T15:29:21.536Z', 'Osito.AI', 'https://osito.ai', 'images/osito-ai.webp', 'images/osito-ai-icon.svg', '{"what":"Osito.AI is an AI-powered travel planner that simplifies the process of organizing group events by finding the perfect hotel with special group rates worldwide.","who":"Osito.AI is designed for event planners, executive assistants, office managers, and anyone responsible for organizing company retreats, family reunions, sports team accommodations, or wedding venue bookings.","best_features":["Streamlined event planning process by eliminating extensive research and multiple phone calls","Access to special group rates at over 600,000 hotels worldwide","Osito Rewards program offering free nights for personal travel"]}', 'Osito.AI is an AI-powered travel planner that simplifies the process of organizing company retreats, family reunions, sports team accommodations, or wedding venue bookings. The platform offers a streamlined approach to event planning by eliminating the need for extensive research, multiple phone calls, and email chains. With Osito, users can easily find the perfect hotel for group events with ease, taking advantage of special group rates at over 600,000 hotels worldwide.', NULL, '["Event Planning","AI Solutions"]');
