INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('9539a327-a59d-46cf-9cbf-b735aa54ecfe', '2024-04-12T15:29:21.536Z', 'Osmosis', 'https://osmosis.fm/home', 'images/osmosis.webp', 'images/osmosis-icon.webp', '{"what":"Osmosis is an AI-driven digital platform that provides personalized, curated insights from expert discussions in various categories. It optimizes user engagement by filtering substantial conversations into easily digestible segments, allowing users to gain valuable knowledge in a minimal time frame.","who":"Osmosis is designed for individuals who seek efficient absorption of information from podcasts and expert discussions. It caters to those looking to stay informed and updated on various topics without investing a significant amount of time.","best_features":["AI-driven platform for personalized insights from expert discussions","Efficiently sifts through podcast backlog to extract meaningful conversation segments","Offers insights in five minutes or less, ideal for quick knowledge acquisition"]}', 'Osmosis is an AI-driven digital platform that provides personalized, curated insights from expert discussions. It optimizes user engagement by filtering substantial conversations into easily digestible segments, allowing users to gain valuable knowledge in a minimal time frame. Osmosis facilitates different categorical preferences such as news, tech, finance, parenting, self-care, and more, enabling users to personalize their ''flow'' based on their interests.', NULL, '["Knowledge Acquisition","Podcast Insights","Personalized Learning"]');
