INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('72c0570e-58e8-41d3-9f98-7f5bda3130f9', '2024-04-12T15:29:21.537Z', 'otto-Bot AI', 'https://www2.otto-js.com/otto-bot-ai', 'images/otto-bot-ai.webp', 'images/otto-bot-ai-icon.svg', '{"what":"otto-Bot AI is an AI security and compliance expert tool designed to assist professionals in safeguarding websites and navigating complex compliance requirements.","who":"otto-Bot AI is for professionals seeking reliable and efficient support in the areas of security and compliance.","best_features":["Engage in real-time conversations and receive prompt answers to security and compliance inquiries.","Review scripts for malware and receive expert guidance on safeguarding websites.","Receive personalized assistance with compliance tasks and save conversations for future reference."],"pricing":[]}', 'Otto-Bot AI is the world''s first AI security and compliance expert tool that provides groundbreaking AI capabilities for security and compliance tasks. It offers real-time conversations, comprehensive security research, and reliable compliance assistance. Users can review scripts for malware, receive personalized compliance assistance, and save conversations for future reference.', NULL, '["Security Tools","Compliance"]');
