INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('bafa0611-b645-4434-b4e4-2f04da367ff3', '2024-04-12T15:29:21.537Z', 'Outboundly', 'https://outboundly.app', 'images/outboundly.webp', 'images/outboundly-icon.webp', '{"what":"Outboundly is an AI-powered Chrome extension designed for personalized sales outreach, enabling users to craft hyper-personalized cold emails and LinkedIn messages with just one click.","who":"Outboundly is for sales professionals, marketers, and business owners looking to streamline their prospecting and outreach workflows by leveraging AI technology for personalized messaging.","best_features":["AI-powered personalized emails and LinkedIn messages with one click","Instantly generate highly personalized messages based on prospect''s online presence","Efficiently scale cold outreach efforts while maintaining a personalized touch"],"pricing":[]}', 'Outboundly is an AI-powered Chrome extension that simplifies cold outreach by generating personalized prospecting messages based on the prospect''s website, blog posts, and social media profiles. It allows users to easily scale their outreach efforts while maintaining a personalized touch, resulting in significantly higher response rates. With Outboundly, users can streamline their prospecting workflows and enhance their LinkedIn messaging capabilities for seamless communication with prospects.', NULL, '["AI Agents","AI Assistance","Marketing"]');
