INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('23a8905f-0cd0-4d14-9f8b-2da6a8471e5a', '2024-04-12T15:29:21.544Z', 'Pansophic', 'https://pansophic.co/', 'images/pansophic.webp', 'images/pansophic-icon.webp', '{"what":"Pansophic is an AI-powered tool designed for conducting user interviews and gathering feedback. It streamlines the process of collecting user insights through AI agents.","who":"Pansophic is ideal for product managers, UX researchers, designers, and anyone involved in user research and feedback gathering.","best_features":["AI-powered user interviews for efficient data collection","Eliminates the need for interview scheduling, making it convenient for users and participants","Comprehensive analysis of user feedback with access to insights, summaries, and transcripts"],"pricing":[{"plan":"Pay-per-use model","price":null,"billing_cycle":null,"original_price":null,"discount":"First 5 interviews free, subsequent interviews paid"}]}', 'Pansophic is an AI-powered tool that simplifies the process of conducting user interviews and gathering valuable feedback. Users can train AI agents to conduct interviews, eliminating the need for scheduling. The tool offers convenient access to interview insights, summaries, and transcripts for comprehensive analysis. Pansophic aims to help users scale their user research efforts effectively and improve their products based on user feedback.', NULL, '["User Research","Feedback Gathering"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('40e33186-7196-460b-b3db-2c42237522a0', '2024-04-12T15:29:21.544Z', 'PantryBot', 'https://pantry.bot/', 'images/pantrybot.webp', 'images/pantrybot-icon.webp', '{"what":"PantryBot is an AI-enabled recipe generator that offers users unique recipe suggestions based on the ingredients they input. It also estimates the nutritional content and serving size of the proposed recipe, making it ideal for those monitoring their nutritional intake.","who":"PantryBot is for individuals who enjoy cooking and are looking for inspiration and guidance in the kitchen. It is also beneficial for those who are conscious about their nutritional intake and want to make informed food choices.","best_features":["AI-enabled recipe suggestions based on user input","Estimation of nutritional content and serving size","Personalized account creation for added security and customization"]}', 'PantryBot is an AI-enabled recipe generator that provides users with unique recipe suggestions based on the ingredients they input. It also estimates the nutritional content and serving size of the proposed recipe, making it a valuable tool for those monitoring their nutritional intake. Users are required to create an account for personalized use, and PantryBot is not limited to pantry items, offering utility in diverse culinary scenarios.', NULL, '["AI Tools","Health & Wellness"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('d8b1a887-5232-48f9-8507-29cfae218d81', '2024-04-12T15:29:21.544Z', 'PaperClip', 'https://www.paperclipapp.xyz/', 'images/paperclip.webp', 'images/paperclip-icon.webp', '{"what":"PaperClip is a tool designed to support AI researchers in their daily work of reviewing papers in machine learning, computer vision, and natural language processing.","who":"PaperClip is for AI researchers who need help in organizing and memorizing important details from various sources such as AI research papers, ML blog posts, and news articles.","best_features":["Ability to memorize and remember crucial information from readings","Privacy-conscious AI functions running locally","Offline access and flexibility to reset saved data"],"pricing":[]}', 'PaperClip is an indispensable tool for AI researchers, providing efficient organization, quick retrieval, and offline support for their daily paper reviewing tasks across machine learning, computer vision, and natural language processing.', NULL, '["Research Tools","Productivity Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('ff3c2f39-0313-4a53-a523-fbda51c03ac6', '2024-04-12T15:29:21.544Z', 'PaperClipapp', 'https://www.paperclipapp.xyz/', 'images/paperclipapp.webp', 'images/paperclipapp-icon.webp', '{"what":"PaperClipapp is an AI tool designed to help researchers and professionals efficiently manage and retrieve important findings from research papers, blog posts, and news articles.","who":"PaperClipapp is for researchers and professionals in the fields of AI, machine learning, computer vision, and natural language processing who want to keep track of daily papers, memorize details, and easily find back important findings.","best_features":["Local AI processing for privacy and security","Minimal disk usage of 0.12MB","Offline support and search without internet connection"],"pricing":[]}', 'PaperClipapp is an AI tool that helps users keep track of their daily AI papers review, memorize details from research papers in AI, machine learning, computer vision, and natural language processing, and easily find back important findings. The tool runs locally on the user''s device, ensuring privacy and security, with minimal disk usage. Users can reset saved data with one click, delete data entirely, and benefit from offline support for search. PaperClipapp is a valuable tool for researchers and professionals seeking efficient knowledge management.', NULL, '["AI Tools","Data Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('fd3a994c-313d-464c-b379-eb39d08f1959', '2024-04-12T15:29:21.544Z', 'Paperclips Copilot', 'https://www.paperclips.app/copilot', 'images/paperclips-copilot.webp', 'images/paperclips-copilot-icon.webp', '{"what":"Paperclips Copilot is a browser extension that helps create smart flashcards from anywhere on the internet in a few simple clicks.","who":"Paperclips Copilot is for students, professionals, or anyone looking to easily create high-quality flashcards from online sources.","best_features":["Automatically generate quality flashcards from any web source","Export generated flashcards to various formats including Anki, Quizlet, CSV, and PDF","Highlights feature in Copilot to choose critical concepts for flashcards"],"pricing":[]}', 'Paperclips Copilot is a browser extension that works alongside the Paperclips AI tool to help users create smart flashcards from any online source with just a few clicks. It offers the ability to automatically generate quality flashcards, export them to different formats, and choose key concepts to include in the flashcards. Paperclips Copilot is a convenient and efficient tool for boosting learning and retaining information.', NULL, '["AI Tools","Study Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('2350029e-e0ea-40b8-ba8f-ae30da383ba4', '2024-04-12T15:29:21.545Z', 'Paperclips', 'https://www.paperclips.app/', 'images/paperclips.webp', 'images/paperclips-icon.webp', '{"what":"Paperclips is an AI-based tool that generates high-quality flashcards from PDFs of course notes using ChatGPT, making it easier for students to learn and prepare for exams.","who":"Paperclips is designed for high school and college students who want to simplify the process of preparing for exams by creating high-quality flashcards from their course notes.","best_features":["AI-based technology ensures high-quality and relevant flashcards","Platform flexibility for exporting to Anki, Quizlet, Zorbi, PDF, and CSV","Seamless integration with GoodNotes and Notability for easy access across devices"]}', 'Paperclips is an AI-based tool that generates high-quality flashcards from PDFs of course notes using ChatGPT, making it easier for students to learn and prepare for exams. It offers platform flexibility, seamless integration with note-taking apps, and unlimited flashcard generation. Users can choose from monthly, four-monthly, or yearly pricing plans based on their needs.', NULL, '["Study Tools","AI Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('47626339-b974-4a98-93ef-a93e283d5ebf', '2024-04-12T15:29:21.545Z', 'Papertalk', 'https://papertalk.io/', 'images/papertalk.webp', 'images/papertalk-icon.webp', '{"what":"PaperTalk.io is an AI-driven platform designed to enhance research paper understanding by providing concise explanations and audiobooks. It simplifies complex research papers for a wide audience.","who":"PaperTalk.io is for researchers, students, academics, and anyone interested in accessing and understanding research papers more easily. It aims to bridge the gap between complex academic content and the general public.","best_features":["Generative AI technology for concise explanations and audiobooks","LLM capabilities for comprehensive breakdowns of papers","500-word explanations and 5-minute audiobooks for quick understanding"]}', 'PaperTalk.io is an AI-driven platform that simplifies research paper understanding by providing concise explanations and audiobooks. It utilizes Generative AI technology to break down complex papers into easily digestible content. With features like 500-word explanations and 5-minute audiobooks, PaperTalk.io aims to make academic knowledge accessible to a wider audience. The platform''s mission is to empower communities by bridging the gap between research papers and the general public.', NULL, '["Research Tools","Education"]');
