INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('feacb305-c045-430d-87e2-0a39a1713d26', '2024-04-12T15:29:21.547Z', 'Parrot AI', 'https://parrot.ai/', 'images/parrot-ai.webp', 'images/parrot-ai-icon.webp', '{"what":"Parrot AI is an AI tool designed for businesses to collect, transcribe, and securely store important business experiences such as video conferences, presentations, or customer calls. It helps turn recorded conversations into searchable and shareable knowledge for teams.","who":"Parrot AI is ideal for businesses of all sizes looking to better manage and leverage important recorded conversations and knowledge for long-term use. It is especially beneficial for teams that rely on virtual meetings and collaboration.","best_features":["Automatically transcribe and store meetings from various platforms like Zoom, Microsoft Teams, and Google Meet.","AI assistant organizes conversational knowledge and prepares meeting summaries by topic and action items.","Secure workspace prioritizes privacy and data security, ensuring recorded content is safe and accessible only to authorized users."],"pricing":[{"plan":"Free Plan","price":0,"billing_cycle":"Monthly","original_price":0,"discount":"N/A","features":["AI-assisted search functionality","Organize knowledge in video-rich, editable pages","Secure data storage"]},{"plan":"Premium Plan","price":29.99,"billing_cycle":"Monthly","original_price":29.99,"discount":"N/A","features":["Automated transcription of meetings","AI thought partner for brainstorming and drafting","Advanced search capabilities"]}]}', 'Parrot AI is an AI tool that helps businesses capture, transcribe, and store important business conversations for future reference and knowledge sharing. It enables teams to easily search, summarize, and share recorded content, fostering collaboration and idea sharing. With a focus on security and privacy, Parrot AI ensures that sensitive information remains protected and accessible only to authorized users.', NULL, '["AI Tools","Meeting Assistance","Data Management"]');
