INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('60a7e424-6e2b-4940-9ec5-6111c327aeba', '2024-04-12T15:29:21.562Z', 'Photo Restore', 'https://photorestore.io', 'images/photo-restore.webp', 'images/photo-restore-icon.webp', '{"what":"PhotoRestore.io is a free AI-powered photo restoration service that revitalizes old or damaged photos using advanced algorithms. It repairs blurry, faded, or damaged images, enhances their quality and clarity, removes backgrounds, increases image resolution, and colorizes photos.","who":"This product is for individuals looking to restore old or damaged photos, preserve precious memories, and create clear and high-quality images for keepsakes.","best_features":["AI-powered photo restoration for repairing blurry, faded, or damaged images","Background removal, image resolution enhancement, and photo colorization","Focus on privacy and data security for safe preservation of precious memories"],"pricing":[]}', 'PhotoRestore.io offers a free AI-powered photo restoration service aimed at revitalizing old or damaged photos. Using advanced algorithms, it repairs blurry, faded, or damaged images, enhancing their quality and clarity. Additionally, the tool can remove backgrounds, increase image resolution, and colorize photos, breathing new life into cherished memories. With a focus on privacy and data security, PhotoRestore.io ensures the safe preservation of precious moments.', NULL, '["AI Services","Artificial Intelligence","Photography"]');
