INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('d1b224ca-2bf3-4ec7-95a2-b8bb8777510e', '2024-04-12T15:29:21.576Z', 'Pixplain', 'https://www.getmerlin.in/pixplain', 'images/pixplain.webp', 'images/pixplain-icon.svg', '{"what":"Pixplain is an AI-powered tool developed by Merlin designed to unlock visual content. The application allows users to interact with images and videos intuitively, enhancing user experience with AI clarity.","who":"Pixplain is for individuals and businesses looking to easily analyze and understand visual content using AI technology. It is suitable for content creators, designers, marketers, educators, and anyone who works with images and videos.","best_features":["Utilizes top AI models such as GPT-4, GPT vision, Claude, and Llama for efficient outcomes","Features GPT Vision for superior image analysis and results","Allows users to easily copy, update, or change prompts for a smoother creative process"],"pricing":[]}', 'Pixplain is an AI-powered tool by Merlin that enables users to interact with images and videos on any website. By adding an extension to the browser, users can activate the Pixplain Capture Bar to select and capture desired areas on the screen. The tool utilizes advanced AI models like GPT-4 and GPT Vision to provide insightful responses to user queries. With the flexibility to move the Pixplain window for optimal viewing and easy prompt customization, Pixplain enhances the visual content experience for users.', NULL, '["Image Editing","Content Creation","AI Solutions"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('ed5cc250-0c3a-4054-98ac-1ac005cc803d', '2024-04-12T15:29:21.576Z', 'Plagium AI Detector', 'https://www.plagium.com/en/ai_detector', 'images/plagium-ai-detector.webp', 'images/plagium-ai-detector-icon.webp', '{"what":"Plagium Originality AI Detector is a tool designed to detect and identify AI-generated text that might be copied or paraphrased from other sources. It uses sophisticated algorithms and machine learning techniques to compare the input with a massive database of AI-created content.","who":"This product is for writers, researchers, educators, and content creators who want to ensure the authenticity of their text and avoid plagiarism.","best_features":["Advanced GPT-based plagiarism detection mechanism","Multilingual support for detecting plagiarism in various languages","API access for integration with other platforms"],"pricing":[{"plan":"Plagium Originality AI Detector","price":0.05,"billing_cycle":"per 1000 characters","original_price":0.04,"discount":"per page","features":["AI-generated text detection","Multilingual support","API access"]}]}', 'Plagium Originality AI Detector is a tool designed to detect and identify AI-generated text that might be copied or paraphrased from other sources. It provides a report displaying the probability of the input text being machine-generated and supports multiple languages for enhanced detection capabilities.', NULL, '["Plagiarism Detection","Text Analysis","Content Authenticity"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('5ac1341c-ce27-49d3-8c71-78b33bc94114', '2024-04-12T15:29:21.576Z', 'Plainscribe', 'https://www.plainscribe.com/', 'images/plainscribe.webp', 'images/plainscribe-icon.webp', '{"what":"Plainscribe is a transcription, translation, and summarization tool that helps users effortlessly convert their media files into text, break language barriers, and distill key insights from the content.","who":"Plainscribe is designed for individuals and businesses who need to transcribe audio and video files, translate content into different languages, and summarize text for quick insights.","best_features":["Effortlessly transcribe audio and video files","Translate content into 50+ languages","Summarize text for key insights"]}', 'Plainscribe is a versatile tool that simplifies the process of transcribing, translating, and summarizing audio and video files. With support for multiple languages, large file sizes, and a pay-as-you-go model, Plainscribe offers convenience and efficiency for users looking to extract valuable insights from their media content.', NULL, '["Transcription","Translation","Summarization"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('4c490d0a-589b-49f2-b679-6bac219bf80d', '2024-04-12T15:29:21.576Z', 'Plais', 'https://plais.io/', 'images/plais.webp', 'images/plais-icon.webp', '{"what":"Plais is an AI tool that transforms ordinary photos of interiors into extraordinary interior designs, saving time and money for property professionals, interior designers, and rental owners.","who":"Plais is designed for property agents, interior designers, Airbnb and rental owners who want to showcase or visualize interior design ideas for new spaces.","best_features":["AI technology for generating stunning interior ideas and visualizing new spaces","Simple three-step process: upload, style, and process photos","Range of styles to choose from and customization options for room type, colors, and image resolution"]}', 'Plais is an AI tool that enhances and transforms ordinary photos of interiors into extraordinary interior designs. It offers a simple three-step process and a range of styles to choose from, catering to property agents, interior designers, and rental owners. The Pro Plan provides additional features for a monthly or annual subscription.', NULL, '["Interior Design","Real Estate"]');
