INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('c57c7042-2894-4f76-ac4d-a51fc55c33ab', '2024-04-12T15:29:21.577Z', 'Plan With Whimsy', 'https://www.planwithwhimsy.com/', 'images/plan-with-whimsy.webp', 'images/plan-with-whimsy-icon.webp', '{"what":"Whimsy is an all-in-one planning tool designed to revolutionize the wedding planning experience by providing a consolidated dashboard for seamless vendor search, communication, and management.","who":"Whimsy is for couples actively planning their wedding, as well as wedding planners and vendors in the San Francisco Bay Area.","best_features":["Consolidated dashboard for all planning resources","Guidance through every step of the wedding planning process","Saves users hours each week by centralizing tasks and resources"],"pricing":[]}', 'Whimsy is an innovative wedding planning tool developed by passionate product managers Shreya and Megha from San Francisco. It aims to simplify and streamline the wedding planning process by providing a centralized platform for finding vendors, negotiating contracts, and visualizing planning progress. Whimsy eliminates the need for multiple tools and websites, saving users time and reducing stress, ultimately transforming wedding planning into a whimsical and enjoyable experience.', NULL, '["Wedding Planning"]');
