INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('a76c179e-c6fd-47ea-a0a2-b6190daccd8c', '2024-04-12T15:29:21.579Z', 'Planio', 'https://planio.io/', 'images/planio.webp', 'images/planio-icon.webp', '{"what":"Planio is an AI-powered planning and knowledge base tool designed for technical documentation and APIs. It offers a comprehensive solution to consolidate and centralize all technical knowledge in one easily accessible location.","who":"Planio is designed for software developers, technical writers, project managers, and anyone involved in software and API planning and specifications.","best_features":["Comprehensive knowledge capture with diagrams, spreadsheets, API specifications, Gantt charts, and rich-text documents.","Efficient design of data flows and integration elements for developer-friendly design.","Visual enhancements for improved business comprehension and seamless collaboration across teams."],"pricing":[]}', 'Planio is an AI-powered planning and knowledge base tool that streamlines the process of technical documentation for software and API planning. It offers a range of features including diagrams, spreadsheets, API specifications, Gantt charts, and rich-text documents to facilitate comprehensive knowledge capture and efficient design of data flows. With integration with Swagger for API specifications and visual enhancements for improved business comprehension, Planio is a robust solution for software development and project management.', NULL, '["Project Management","API Documentation","Knowledge Base"]');
