INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('b231b3a1-4a84-4aeb-90ab-a80d2d28bf05', '2024-04-12T15:29:21.593Z', 'Postcrest', 'https://postcrest.com/ai-content-generation/faceswap', 'images/postcrest.webp', 'images/postcrest-icon.webp', '{"what":"Postcrest is an AI-powered tool that provides a face-swapping feature for images, allowing users to seamlessly swap faces in photographs for a realistic edit.","who":"Postcrest is designed for content creators and marketers who want to experiment with visuals in a strategic and playful way, as well as individuals looking to enhance their images with high-quality face swaps.","best_features":["Ultra-precise matching for perfect face swaps with aligned skin tones and facial features in high resolution.","Fast transformations with quick results and minimal user intervention required.","Ability to enhance images beyond face swapping for a polished, high-quality finish."],"pricing":[]}', 'Postcrest is an AI-powered tool that offers a face-swapping feature for images, allowing users to transform photographs by swapping faces seamlessly. It provides a simplified process where users upload or generate images, choose the face to swap, let the AI execute the transformation, and download the manipulated image. Postcrest prides itself on ultra-precise matching, fast transformations, and the ability to enhance images for a polished finish. It is ideal for content creators, marketers, and individuals looking to elevate their visual content.', NULL, '["AI Tools","Art Generation","Content Creation"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('11d331be-283b-4254-9cdb-7e31ab139e1d', '2024-04-12T15:29:21.593Z', 'Poster studio', 'https://www.posterstudio.ai/', 'images/poster-studio.webp', 'images/poster-studio-icon.webp', '{"what":"Poster Studio is an AI-powered tool designed to maximize return on investment (ROI) for ad and social creatives by generating captivating ads effortlessly. It uses AI to create unique creatives from scratch and offers features such as conversion-focused ad generation, high-quality poster messaging, and AI-powered product placement technology.","who":"Poster Studio is ideal for businesses, marketers, social media managers, small business owners, entrepreneurs, freelancers, and non-profit organizations looking to enhance their brand impact, simplify the creative process, and achieve exceptional results in their ad campaigns.","best_features":["Generate conversion-focused creatives with unlimited variations in a minute","Produce high-quality poster messaging with a Chat GPT-powered custom creative messaging generator","Utilize AI-powered product placement/background removal technology for efficient product promotion"]}', 'Poster Studio is an AI-powered tool that revolutionizes the way creatives are created for ad and social campaigns. It offers seamless ad generation, real-time AI training, creative scoring, and Chat GPT content creation. The tool supports platform-independent ads on popular platforms like Pinterest, Instagram, and LinkedIn, providing faster creative turnaround times, increased social click-through rates, and optimized cross-platform marketing performance.', NULL, '["AI Tools","Social Media","Marketing"]');
