INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('36862e42-22bf-41fe-beaa-1e9114e83d85', '2024-04-12T15:29:21.596Z', 'PoweredBy', 'https://poweredby.startdate.co/', 'images/poweredby.webp', 'images/poweredby-icon.svg', '{"what":"PoweredBy is an AI Powered Candidate Screening platform designed for enterprises to streamline the process of candidate assessment by matching skills with job requirements.","who":"This product is intended for HR professionals, recruiters, and hiring managers in enterprises looking to efficiently screen and assess job candidates.","best_features":["Advanced screening methods using artificial intelligence to reduce manual effort and eliminate bias in candidate selection.","Seamless integration with Applicant Tracking Systems for a streamlined experience.","Continuous enhancement of the proprietary model to ensure accurate candidate-job matching."],"pricing":[]}', 'AI Powered Candidate Screening is a cutting-edge platform designed to streamline the process of candidate assessment by matching candidate skills with job requirements. It uses advanced screening methods powered by artificial intelligence to reduce manual effort, eliminate bias, and speed up the recruitment process. The tool is integrated into Applicant Tracking Systems for a seamless experience, providing a simplified and efficient approach to analyze compatibility between candidates and job roles.', NULL, '["AI Solutions","Enterprise"]');
