INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('2dde4b85-7a75-4f1f-9743-94a31653d36f', '2024-04-12T15:29:21.608Z', 'Project Blink', 'https://labs.adobe.com/projects/blink/', 'images/project-blink.webp', 'images/project-blink-icon.webp', '{"what":"Project Blink is an experimental AI-powered video editing tool developed by Adobe Labs. It allows users to search for specific parts of a video, edit video transcripts, and create outlines of key moments.","who":"Project Blink is designed for content creators, video editors, and anyone looking to streamline their video editing process with the help of AI technology.","best_features":["AI-powered search functionality to find specific objects, sounds, and moments in videos","Editing video transcripts like text to easily cut, paste, and delete content","Automatic trimming of clips, removal of distractions, and creation of organized video outlines"]}', 'Project Blink is an experimental AI-powered video editing tool that leverages machine learning and artificial intelligence to enhance the video editing process. Users can search for specific parts of a video, edit video transcripts, and create outlines of key moments with ease. The tool automatically trims clips, removes distractions, and improves overall video quality. Project Blink is a project of Adobe Labs, with no pricing information or commercial availability announced yet.', NULL, '["AI-powered","Experimental","Adobe Labs"]');
