INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('e2a620d6-178b-4901-812e-7a6cb1b72a40', '2024-04-12T15:29:21.634Z', 'Quizwhiz', 'https://www.quizwhiz.ai/', 'images/quizwhiz.webp', 'images/quizwhiz-icon.webp', '{"what":"QuizWhiz is an AI tool that generates multiple-choice questions and answers quizzes based on provided text. It is designed to assist educators, trainers, and content creators in quickly creating engaging and interactive quizzes for their audience.","who":"QuizWhiz is for educators, trainers, content creators, and anyone looking to create interactive quizzes based on text content. It is especially useful for those in the education sector, online course creators, and businesses looking to engage their audience through quizzes.","best_features":["AI-powered question generation for efficient quiz creation","Support for up to 1000 runs per month with the free starter plan","Ability to handle input text of up to 3000 words for comprehensive quiz generation"]}', 'QuizWhiz is an AI tool that simplifies the process of creating multiple-choice quizzes by automatically generating questions and answers based on provided text. With support for up to 1000 runs per month and the ability to handle input text of up to 3000 words, QuizWhiz is a valuable tool for educators, trainers, and content creators.', NULL, '["AI Tools","Content Creation"]');
