INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('1cdad5b1-096a-45d9-a5a6-5928be0fcd41', '2024-04-12T15:29:21.635Z', 'Quotid', 'https://www.quotid.me/', 'images/quotid.webp', 'images/quotid-icon.webp', '{"what":"Quotid is an AI-driven tool designed to assist users in learning a new language through daily lessons. It provides diverse content generated by AI to make the learning journey valuable and realistic.","who":"Quotid is ideal for individuals looking to learn a new language, especially those with busy schedules who need a manageable and realistic approach to language learning.","best_features":["AI-generated content for diverse speech and topics","Flashcard system with Open Spaced Repetition algorithm for vocabulary practice","Ability to generate custom lessons and track lesson history"],"pricing":[]}', 'Quotid is a language learning tool that offers daily lessons generated by AI to help users learn a new language in a manageable and realistic way. It provides diverse content, a flashcard system for vocabulary practice, and the ability to create custom lessons.', NULL, '["Language Learning","AI Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('21b976f9-8860-490b-aab5-7787a3439514', '2024-04-12T15:29:21.635Z', 'Quuu', 'https://quuu.co/', 'images/quuu.webp', 'images/quuu-icon.webp', '{"what":"Quuu is an AI-driven platform that automatically curates and schedules engaging content for social media sharing, tailored to each brand''s unique tone and audience. It helps optimize posting schedules for increased visibility and engagement.","who":"Quuu is designed for social media marketers, agencies, bloggers, podcasters, and businesses looking to enhance their online presence through curated and engaging content.","best_features":["Intelligent Content Curation with AI","Daily Automation for optimized post timing","Variety of content types for increased engagement"],"pricing":[]}', 'Quuu is an AI-powered social media assistant that simplifies content curation and scheduling for brands. With features like brand tone analysis, audience engagement insights, and daily automation, Quuu helps users grow their audience on platforms like Facebook, LinkedIn, and more.', NULL, '["Content Curation","AI Assistant","Marketing Tool"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('8cfff226-063c-47e0-a3e6-246338ec9974', '2024-04-12T15:29:21.635Z', 'Qwokka', 'https://api.whatsapp.com/message/WGPYTBDGAW74M1', 'images/qwokka.webp', 'images/qwokka-icon.webp', '{"what":"Qwokka is a WhatsApp Business Account AI-powered tool designed to help businesses reach and engage with customers on the popular messaging platform, WhatsApp.","who":"This product is suitable for businesses of all sizes and industries looking to enhance their customer service, increase engagement, and drive business growth.","best_features":["End-to-end encryption and privacy controls for secure and private communication with customers.","Various messaging options including text, voice, stickers, GIFs, and more for personalized and interactive engagement.","Free messaging and calling services, simplifying group conversations and community building for efficient communication."],"pricing":[]}', 'The WhatsApp Business Account AI-powered tool enables businesses to establish a professional presence, communicate securely with customers, and leverage various messaging options to engage with customers in a personalized and interactive way. It simplifies group conversations, provides free messaging and calling services, and helps businesses stay connected with customers globally.', NULL, '["AI Tools","Business Tools","Customer Support"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('a0b70ad3-db06-49ba-9a27-408bd6235692', '2024-04-12T15:29:21.635Z', 'R AI', 'https://r-ai.dev/', 'images/r-ai.webp', 'images/r-ai-icon.svg', '{"what":"R.Ai is an email communication tool that utilizes a large language model to understand and mimic your communication style, providing personalized responses in your own writing style. It is designed for individuals and organizations looking to automate and streamline their email interactions.","who":"R.Ai is ideal for professionals, teams, investors, and customer-facing roles who want to enhance their email communication efficiency. It caters to those who value personalized responses and seek to save time managing their inbox.","best_features":["Personalized responses in your writing style","Full context understanding of email conversations","Integration with Google sign-in for easy access"],"pricing":[]}', 'R.Ai is an email communication tool powered by GPT Chat, offering personalized responses in your own writing style based on your email interactions. It is trained on all your email communications and soon to be available for slack and google docs. By integrating with Google sign-in, R.Ai revolutionizes email management, saving time and improving productivity for individuals and organizations.', NULL, '["Email Management","AI Assistance","Communication"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('43a7c0ff-304c-4f8e-941c-2c05bfc4a93f', '2024-04-12T15:29:21.636Z', 'R.O.B. (Robot Of Business)', 'https://www.robotofbusiness.com/', 'images/r-o-b--(robot-of-business).webp', 'images/r-o-b--(robot-of-business)-icon.webp', '{"what":"R.O.B. (Robot Of Business) is an AI tool that automates the website creation process by generating customized content without the need for manual writing. It aims to simplify the website development process and provide high-quality content that resonates with the business and engages visitors.","who":"R.O.B. is designed for business owners, entrepreneurs, and individuals looking to quickly create professional websites without the hassle of writing content. It is ideal for those who want to streamline their website creation process and focus on other aspects of their business.","best_features":["Automated content generation for stress-free website creation","Customized content that reflects the business and engages visitors","Quick turnaround time with a first draft available in as little as 15 minutes"]}', 'R.O.B. (Robot Of Business) is an AI tool that creates customized websites without the need for writing content. It offers a stress-free writing process and creates high-quality content that reflects the business and engages visitors. R.O.B. takes time to understand the project and customer preferences and provides a great first draft in as little as 15 minutes. The mission of R.O.B. is to remove barriers and allow people to quickly act on their website ideas.', NULL, '["Content Creation","Website Builder","Automation"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('a04f417f-1162-4325-a392-202f0a2ad1e5', '2024-04-12T15:29:21.636Z', 'Raay', 'https://raay.io/', 'images/raay.webp', 'images/raay-icon.webp', '{"what":"Raay is an AI tool designed to simplify the process of creating, sharing, running, and analyzing data with forms and surveys. It utilizes artificial intelligence technology to provide actionable insights quickly and efficiently.","who":"Raay is for individuals and businesses looking to streamline their data collection and analysis processes. It is ideal for anyone who wants to create forms and surveys with the assistance of AI technology.","best_features":["Highly Integratable: Collect responses on any device and embed forms on websites or mobile apps through APIs.","Bias Detection: Ensures accuracy and fairness of results by identifying and correcting potential biases in responses.","Meaningful Insights: Utilizes AI to generate insights through clustering, visualization, and outcomes."]}', 'Raay is an AI tool that simplifies the process of creating and analyzing forms and surveys. It offers features such as highly integratable forms, bias detection system, and meaningful insights generated through AI technology. Raay provides different pricing plans to cater to various needs, including a free starter plan, a professional plan for $10 per month, and a custom business plan for more extensive usage.', NULL, '["AI Tools","Survey Creation","Data Collection"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('15a36644-2ae8-4d32-8dbc-04d0341040ba', '2024-04-12T15:29:21.636Z', 'Radiant AI', 'https://www.radiantai.health/', 'images/radiant-ai.webp', 'images/radiant-ai-icon.svg', '{"what":"Radiant AI is an AI-powered platform configured to provide various services targeted towards healthcare learners and medical professionals. Its primary feature is an AI medical chatbot, Radiant AI Chat, built on its proprietary framework, OSLER-platformLLM.","who":"Radiant AI is mainly designed for medical professionals or medical learners, but everyone is welcome to use it.","best_features":["Explainable AI medical chatbot that shows the source of information","Retrieval-augmented generation for quick and accurate responses","Access to LLMs specifically trained on unbiased clinical data"],"pricing":[{"plan":"Free Tier","price":0,"billing_cycle":"N/A","original_price":0,"discount":"N/A","features":["Unlimited chats","Longer response length","Longer chat history"]},{"plan":"Premium Services","price":"Contact for pricing","billing_cycle":"N/A","original_price":"Contact for pricing","discount":"N/A","features":["Secure, on-premise HIPPA-compliant chat","Integration with EMR","Chat with your own documents"]}]}', 'Radiant AI is an AI-powered platform with an AI medical chatbot, Radiant AI Chat, designed for healthcare learners and medical professionals. It offers explainable AI, retrieval-augmented generation, access to LLMs trained on unbiased clinical data, and data integrations for maximizing insights.', NULL, '["AI Solutions","Medical Chatbot","Clinical Data Integration"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('162b0025-213d-4265-a612-42f99938a983', '2024-04-12T15:29:21.636Z', 'RadiantAI', 'https://chat.radiantai.health/', 'images/radiantai.webp', 'images/radiantai-icon.webp', '{"what":"Radiant Chat is a tool designed for medical professionals to easily access reference material for medical inquiries. It is a comprehensive repository of information to support decision-making.","who":"Radiant Chat is for medical professionals who seek additional resources to enhance their knowledge and decision-making process in the medical field.","best_features":["User-friendly interface for easy navigation and exploration of medical reference material.","Ability to ask a wide range of medical queries and receive informative responses.","Encourages users to exercise professional judgment when interpreting reference material."],"pricing":[]}', 'Radiant Chat is a tool specifically designed for medical professionals to access reference material for medical inquiries. It serves as a comprehensive repository of relevant information, allowing users to ask medical questions, describe symptoms, and receive informative responses. The tool is not intended to provide medical advice but rather to support decision-making processes by providing valuable insights. Users can log in or sign up to gain access to this tool and are encouraged to exercise their professional judgment when interpreting the retrieved information.', NULL, '["Medical Reference","Decision Support","Professional Tools"]');
