INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('3dcae8ed-3fcc-4925-a74a-3e8a88276fc7', '2024-04-12T15:29:21.637Z', 'RadioNewsAI', 'https://radionewsai.com/', 'images/radionewsai.webp', 'images/radionewsai-icon.webp', '{"what":"RadioNewsAI is an AI-powered platform that provides local radio stations with realistic news anchors to generate newscasts. The tool allows users to import news sources and customize newscast formats using a user-friendly editor.","who":"RadioNewsAI is designed for local radio stations looking to automate their news broadcasting process and reduce costs associated with running a full newsroom.","best_features":["AI-generated realistic news anchors for lifelike newscasts","User-friendly drag and drop editor for easy customization","Option to schedule regular updates and personalize announcements"],"pricing":[{"plan":"5 stories per day","price":49,"billing_cycle":"monthly","original_price":0,"discount":"Start 7-day free trial","features":["Unlimited newscasts","50 minutes of AI-generated audio per month included","Additional usage-based AI audio generation available"]}]}', 'RadioNewsAI is an AI-powered platform that provides local radio stations with realistic news anchors to generate newscasts. Users can import news sources, customize newscast formats, and schedule regular updates with personalized announcements. The tool offers ultra-realistic AI voices designed for news delivery and includes a review and approval option for each newscast before going live.', NULL, '["Audio Editing","News","Radio Stations"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('6d244962-7744-42fc-bdfe-0f7fa0e1283a', '2024-04-12T15:29:21.637Z', 'Raia', 'https://raia.live/', 'images/raia.webp', 'images/raia-icon.webp', '{"what":"Raia is an intelligent remote assistant designed to revolutionize engagement with data by providing tailored insights and automating tasks based on specific data needs.","who":"Raia is for data leaders, data analysts, product managers, developers, and businesses looking to boost productivity, make data-driven decisions, and automate data analysis tasks.","best_features":["Interactive data interaction and tailored insights delivery","Automation of crucial tasks and transformation of insights into actions","Proactive analysis for strategizing based on future predictions"],"pricing":[]}', 'Raia is an intelligent remote assistant that transforms static data into a dynamic resource by interacting with users, providing tailored insights, and automating tasks based on specific data needs. It boosts productivity, enables proactive analysis, and turns complex data sets into actionable insights. Raia is accessible to team members of all technical levels and can handle daily data analysis, team communication, and report creation tasks.', NULL, '["Data Analysis","Automation","Productivity Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('beec780a-ff4a-46bf-95ee-b0a38fd258eb', '2024-04-12T15:29:21.637Z', 'Rails Guard', 'https://railsguard.hoop.dev/', 'images/rails-guard.webp', 'images/rails-guard-icon.svg', '{"what":"Rails Guard is an AI tool that enables live data masking in Rails console sessions, providing passwordless authentication via Google SSO with MFA and AI data masking. It offers auditability, passwordless authentication, and AI data masking as key features.","who":"Rails Guard is designed for developers, IT professionals, and organizations using Ruby on Rails who need to enhance security, automate user management, and ensure compliance with security controls and regulations.","best_features":["Auditability for tracking console operations","Passwordless authentication via Google SSO with MFA","AI data masking for protecting customer data"],"pricing":[]}', 'Rails Guard is an AI tool that enables live data masking in Rails console sessions, providing passwordless authentication via Google SSO with MFA and AI data masking. It offers auditability, passwordless authentication, and AI data masking as key features.', NULL, '["AI Tools","Developer Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('bbe07f81-fe70-43c7-a39d-8eefa8fc7015', '2024-04-12T15:29:21.637Z', 'Raizer', 'https://raizer.app/', 'images/raizer.webp', 'images/raizer-icon.webp', '{"what":"Raizer is an AI tool designed to help startups connect with relevant investors through personalized messages using public data. It streamlines the fundraising process by providing warm introductions to over 500 top VCs, family funds, and angel investors.","who":"Raizer is ideal for startup founders looking to raise venture capital funding and connect with potential investors in a more efficient and effective manner.","best_features":["AI-powered outreach for generating personalized messages","Access to over 55,000 investors with contact information","Warm introductions to top VCs, family funds, and angel investors"]}', 'Raizer is an AI tool that assists startups in finding and reaching relevant investors by crafting hyper-specific messages using public data. It offers warm introductions to over 500 top VCs, family funds, and angel investors, saving time and increasing the likelihood of securing funding.', NULL, '["Startup Resources","Fundraising","Investor Relations"]');
