INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('44c03d8b-3d59-4d96-8b80-e4efa4f0b591', '2024-04-12T15:29:21.638Z', 'Ramp', 'https://ramp.com/intelligence', 'images/ramp.webp', 'images/ramp-icon.webp', '{"what":"Ramp Intelligence is an AI-powered tool designed for finance teams to save time, reduce costs, empower teams, and uncover opportunities. It offers several AI features that are available for early access.","who":"Ramp Intelligence is designed for finance professionals looking to streamline processes and optimize financial management.","best_features":["Vendor Management: Track all vendors and contracts, get software price intelligence, and make successful negotiations.","Smart Accounting: Speed up monthly close processes, automate coding of receipts, and suggest precise coding.","Expenses Management: Automate collection and matching of receipts, generate AI-generated fields, and ensure policy compliance."],"pricing":[]}', 'Ramp Intelligence is an AI-powered tool for finance teams, offering features like Vendor Management, Smart Accounting, and Expenses Management. It helps save time, reduce costs, and empower teams by providing insights and automation for financial processes.', NULL, '["AI Solutions","Expense Management","Vendor Management"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('aeccea94-69b7-4175-be4e-515971518b55', '2024-04-12T15:29:21.638Z', 'Ramsay Roasts', 'https://roast.wordware.ai/', 'images/ramsay-roasts.webp', 'images/ramsay-roasts-icon.webp', '{"what":"Ramsay Roasts is an AI-based tool designed to provide entertaining and engaging food critiques by mimicking the sharp-tongued commentary of a virtual representation of chef Gordon Ramsay. Users can upload images of their dishes to receive humorous ''roasts'' generated through advanced image recognition and text generation algorithms.","who":"This product is for food enthusiasts, home cooks, chefs, and anyone looking to add a fun twist to their food critique experience. It can also be enjoyed as a novelty or party game.","best_features":["AI-based food critique with a humorous twist, emulating Gordon Ramsay''s style.","Advanced image recognition and text generation algorithms for accurate analysis and response generation.","Enhances user experience by providing entertaining and engaging feedback on food dishes."],"pricing":[]}', 'Ramsay Roasts is an AI-based tool developed by Avatech Labs that allows users to receive humorous food critiques in the style of chef Gordon Ramsay. By uploading images of their dishes, users can enjoy entertaining and engaging feedback generated through advanced image recognition and text generation algorithms. This tool not only adds a fun twist to food critique but also showcases the capabilities of AI in digital interactions.', NULL, '["AI-driven","Food & Drink","Humor"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('7778396f-b654-4825-8ef7-5dd7cf239867', '2024-04-12T15:29:21.638Z', 'Random Recipe', 'https://randomrecipeai.netlify.app/', 'images/random-recipe.webp', 'images/random-recipe-icon.svg', '{"what":"The Random Recipes Generator is an AI tool designed to provide users with a wide range of AI-generated recipes suitable for different tastes and dietary preferences. It offers step-by-step instructions and tips from culinary experts, enabling users to enhance their cooking skills.","who":"This product is for individuals seeking quick and easy weeknight meals or indulgent desserts. It caters to both experienced and novice home cooks who want to explore a diverse world of flavors and improve their cooking game.","best_features":["AI-generated recipes for different tastes and dietary preferences","Step-by-step instructions and tips from culinary experts","Covers various dietary needs including vegetarian, gluten-free, and keto-friendly recipes"],"pricing":[]}', 'The Random Recipes Generator is an AI tool that provides users with a vast collection of AI-generated recipes suitable for different tastes and dietary preferences. It offers step-by-step instructions and tips from culinary experts, enabling users to enhance their cooking skills and explore a diverse world of flavors. The tool covers various dietary needs, including vegetarian, gluten-free, and keto-friendly recipes, making it a comprehensive cooking companion for individuals looking to discover new and exciting dishes.', NULL, '["AI Tools","Cooking","Recipe Generator"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('4618847e-e462-45d4-827c-9c15cc6faddf', '2024-04-12T15:29:21.638Z', 'Ranked', 'https://www.ranked.ai/', 'images/ranked.webp', 'images/ranked-icon.webp', '{"what":"Ranked is an AI-supported SEO service that focuses on delivering measurable results through a combination of human-written content, optimization, backlink creation, and leading SEO software. The service is designed to help businesses and agencies improve their organic presence on search engines.","who":"Ranked is for businesses and agencies looking to enhance their SEO strategies and achieve better visibility on search engines. It caters to both small and large companies seeking to improve their online presence and drive more traffic to their websites.","best_features":["Human-written blog content for engaging and intent-driven articles","Fully managed optimization without keyword saturation or spammy techniques","Genuine backlinks through link placements in active content, avoiding spam"]}', 'Ranked is an AI-supported SEO service that offers a comprehensive solution for businesses and agencies to improve their organic search engine rankings. The service includes human-written blog content, optimization, backlink creation, and leading SEO software to deliver effective and data-driven results. Ranked does not use AI to write content to ensure consistent indexing on Google.', NULL, '["AI-supported service","Organic search improvement"]');
