INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('b6ad99f1-e3e0-4e96-a434-e05ed51332bf', '2024-04-12T15:29:21.673Z', 'ResuMetrics', 'https://resumetrics.nl/', 'images/resumetrics.webp', 'images/resumetrics-icon.webp', '{"what":"ResuMetrics is an AI-powered tool designed for automating resume processing. It offers structured data extraction and anonymization of personal identifiable information (PII) from resumes.","who":"ResuMetrics is for professional resume processors looking to streamline and expedite their resume processing workflows with AI capabilities.","best_features":["Structured data extraction from resumes","Anonymization of personal identifiable information (PII)","Automated resume analysis process through an easy-to-use API"]}', 'ResuMetrics is an AI-powered tool for automating resume processing, offering structured data extraction and PII anonymization. It provides an easy-to-use API for complete automation of resume analysis, with future features like resume scoring and vacancy matching. Users can choose from different pricing plans based on the number of credits required for AI actions.', NULL, '["Automation","Data Analysis","Professional Development"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('fa482a9f-5631-4ed7-8f30-82c99b5430a2', '2024-04-12T15:29:21.673Z', 'ResumeWriting', 'https://www.resumewriting.com/', 'images/resumewriting.webp', 'images/resumewriting-icon.svg', '{"what":"ResumeWriting is an AI-powered tool that revolutionizes the process of creating targeted resumes. It uses GPT AI technology to generate an ideal resume in just three minutes.","who":"ResumeWriting is for job seekers looking to create tailored resumes quickly and efficiently. It is especially useful for individuals applying to multiple job openings and wanting to increase their chances of landing their dream job.","best_features":["AI-powered resume generation in just three minutes","Ability to create multiple targeted resumes for different job applications","Range of customizable resume designs to choose from"],"pricing":[{"plan":"Free","price":0,"billing_cycle":"One-time","original_price":0,"discount":"N/A","features":["AI-powered resume generation","Multiple targeted resumes","Customizable resume designs"]}]}', 'ResumeWriting is an AI-powered tool that allows users to create tailored resumes quickly and efficiently. By providing a brief description of their career, users can generate an ideal resume in just three minutes using GPT AI technology. The tool offers the ability to create multiple targeted resumes for different job applications and provides a range of customizable resume designs to choose from.', NULL, '["AI Tools","Job Searching"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('cff4070d-d2b6-4268-9d3a-0c5db0a5b2f8', '2024-04-12T15:29:21.673Z', 'Resumine', 'https://resumine.io/', 'images/resumine.webp', 'images/resumine-icon.webp', '{"what":"Resumine is an AI-powered tool that generates tailored cover letters for job seekers using OpenAI''s GPT-4 language model.","who":"Resumine is designed for job seekers looking to save time and improve their chances of securing job interviews by creating personalized and high-quality cover letters.","best_features":["Automation: Resumine''s AI model can generate multiple cover letters quickly, ideal for users applying to multiple companies.","Personalization: Users can create customized cover letters tailored to each job offer, showcasing relevant skills and strengths.","Boosting Chances: Resumine aims to improve users'' chances of securing interviews by crafting standout cover letters."]}', 'Resumine is an AI-powered tool that generates tailored cover letters for job seekers. It leverages OpenAIs GPT-4 language model to create high-quality cover letters that are indistinguishable from those written by humans. Resumine offers automation, personalization, and aims to boost users'' chances of securing interviews. It is free to use with the option to purchase additional credits for more cover letters. Resumine prioritizes user privacy and offers versatile language support.', NULL, '["AI Tools","AI Writing"]');
