INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('bcb2cf08-6716-4cd8-98d5-48279b4b34d9', '2024-04-12T15:29:21.674Z', 'ReviewGPT.io', 'https://www.reviewgpt.io/', 'images/reviewgpt-io.webp', 'images/reviewgpt-io-icon.webp', '{"what":"ReviewGPT.io is an AI-powered tool designed to enhance the process of reviewing Amazon products by providing advanced analysis of products and customer reviews.","who":"ReviewGPT.io is for online shoppers who want to make more informed purchasing decisions on Amazon by avoiding counterfeit products, fake reviews, and questionable third-party sellers.","best_features":["Advanced AI analysis of products and reviews","Identification of counterfeit products and fake reviews","Enhanced security and safety measures for online shopping"],"pricing":[]}', 'ReviewGPT.io is an AI-powered tool that helps users analyze Amazon products and reviews to make informed purchasing decisions. By utilizing the ReviewGPT Chrome extension, users can avoid counterfeit products, fake reviews, and other potential risks associated with online shopping on Amazon. The tool prioritizes user privacy and continuously improves its services with the involvement of Vercel and OpenAI.', NULL, '["AI Tools","Privacy","Security"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('5caf10b1-1b1b-4336-b350-ddbbaa2efb68', '2024-04-12T15:29:21.675Z', 'Reviewgpt', 'https://reviewgpt.net/', 'images/reviewgpt.webp', 'images/reviewgpt-icon.webp', '{"what":"ReviewGPT is an AI-powered writing tool designed to enhance the quality of writing by generating compelling and creative content using a deep learning model called GPT.","who":"ReviewGPT is for writers looking to improve their writing quality, especially when working with multiple languages and tones. It is suitable for content creators, bloggers, students, professionals, and anyone who wants to enhance their writing skills.","best_features":["AI-powered content generation for compelling and creative writing","Support for multiple languages and tones to cater to diverse writing needs","Free and paid versions available with advanced options and customer service support"]}', 'ReviewGPT is an AI-powered writing tool that enables users to enhance the quality of their writing by generating compelling and creative content using the GPT deep learning model. It supports multiple languages and tones, offering users the ability to review and rewrite their content in various styles. The tool is available in both free and paid versions, with the paid version providing advanced options and customer service support. Created by MagickPen and illustrated by Icons 8, ReviewGPT is a valuable tool for writers seeking to improve their writing efficiency across different languages and tones.', NULL, '["Content Creation","Writing Assistance","AI Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('0b53cfd4-7ebe-48ee-b28d-81547cb6d140', '2024-04-12T15:29:21.675Z', 'Reviewly', 'https://www.reviewly.ai/', 'images/reviewly.webp', 'images/reviewly-icon.webp', '{"what":"Reviewly is an AI-powered platform designed to streamline the process of collecting and managing Google reviews. It automates the collection and response to customer feedback, aiming to enhance online visibility and build customer confidence through genuine, positive reviews.","who":"Reviewly is for businesses of all sizes, from small local businesses to large corporations, looking to improve their online reputation and search rankings through effective review management.","best_features":["AI-crafted review options for quick and hassle-free feedback","High open rate of 97% for SMS review invitations","AI-powered response suggestions for quick and effective customer engagement"]}', 'Reviewly is an AI-powered platform that helps businesses collect and manage Google reviews efficiently. It offers AI-crafted review options, high open rate SMS invitations, and AI-powered response suggestions to enhance customer engagement and online visibility. With a 7-day free trial, businesses can experience the full power of Reviewly without the need for a credit card.', NULL, '["AI Tools","Marketing","Customer Support"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('1dfeda7f-a941-4b6e-9a15-2e9b638efc94', '2024-04-12T15:29:21.675Z', 'ReviewRecon', 'https://yep.so/p/reviewrecon', 'images/reviewrecon.webp', 'images/reviewrecon-icon.webp', '{"what":"ReviewRecon is an AI-powered tool designed to help game development studios leverage game reviews to improve their products. It provides valuable insights by analyzing game reviews and player sentiments, enabling data-driven decision making for future development.","who":"ReviewRecon is for game development studios looking to optimize their game development strategy based on player feedback. It is suitable for both technical and non-technical users who want to enhance player experience and create games that resonate with their audience.","best_features":["AI-powered analysis of game reviews to understand player sentiments and emotions.","Temporal analysis tracking player sentiment evolution over time and correlating it with game updates.","Intuitive dashboard providing key insights, trends, and statistics for informed decision making."],"pricing":""}', 'ReviewRecon is an AI-powered tool that helps game development studios improve their products by analyzing game reviews and player sentiments. It offers insights on strengths and weaknesses, tracks player sentiment evolution over time, and provides an intuitive dashboard for data-driven decision making. By streamlining the review monitoring process, ReviewRecon saves time and effort, enabling developers to refine their game development strategy and optimize for player experience.', NULL, '["AI Tools","Data Analysis","Software Development"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('67f10b28-7bf8-4c39-a0a1-35338cfdaa31', '2024-04-12T15:29:21.675Z', 'Revisely', 'https://revisely.com', 'images/revisely.webp', 'images/revisely-icon.svg', '{"what":"Revisely is an AI-powered learning platform designed to enhance studying and revision for GCSE and A-level exams. It offers AI flashcard generators, AI notes generators, and AI quiz generators to create interactive study materials.","who":"Revisely is for students and professionals looking to strengthen their knowledge in various subjects, particularly those preparing for GCSE, AS, and A-Level exams.","best_features":["AI Flashcard Generator: Quickly convert notes, textbooks, or any documents into interactive learning resources.","AI Quiz Generator: Transform textbooks into interactive quizzes to engage with material and retain information effectively.","Curated Videos and Past Papers: Access to videos and past papers organized by topic for focused study."]}', 'Revisely is an AI-powered learning platform that offers resources such as AI flashcard generators, AI notes generators, and AI quiz generators to enhance studying and revision for GCSE and A-level exams. It provides curated videos and past papers organized by topic, making it easier to focus on specific areas of study. With a user-friendly interface and a comprehensive library of resources, Revisely caters to a diverse range of learners, including students and professionals.', NULL, '["AI Learning Tools","Exam Preparation","Study Resources"]');
