INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('48121ea8-9462-4e84-ab6b-b16d7dd617f7', '2024-04-12T15:29:21.675Z', 'Revocalize AI', 'https://www.revocalize.ai/', 'images/revocalize-ai.webp', 'images/revocalize-ai-icon.webp', '{"what":"Revocalize AI is an innovative tool that allows users to create, clone, and protect unique voices using AI technology. It is like Photoshop for voices, enabling manipulation and transformation of vocal recordings with ease.","who":"Revocalize AI is designed for sound engineers, producers, music enthusiasts, artists, and creators looking to enhance vocal performances and elevate the quality of music productions.","best_features":["Voice synthesizing without constraints, allowing for unlimited natural-sounding voice content creation.","Extensive catalog of voices from different corners of the world, offering unique vocal characters.","Real-time Auto-Tune feature for effortless pitch-perfect performances."],"pricing":[]}', 'Revocalize AI is a tool that offers voice beautification, synthesizing, modulation, and an extensive catalog of voices from different corners of the world. It allows users to explore a wide range of voice models, from male and female voices to rock legends, pop sensations, country stars, and opera maestros.', NULL, '["AI Tools","Audio Editing","Voice Solutions"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('0c10561c-0cf1-46bc-af78-d0e19c3697d3', '2024-04-12T15:29:21.675Z', 'RevoChat', 'https://www.revo-chat.com/', 'images/revochat.webp', 'images/revochat-icon.svg', '{"what":"RevoChat is an AI tool designed to help businesses create and integrate custom chatbots into their websites, enhancing customer service and driving growth through automated customer support.","who":"RevoChat is for businesses looking to improve customer engagement, boost sales, and provide personalized support through AI-powered chatbots.","best_features":["Personalized AI chatbot creation and integration without coding skills","24/7 customer support and personalized interaction","Integration with Shopify and WooCommerce for seamless product suggestions"]}', 'RevoChat is an AI tool that simplifies the process of creating and integrating personalized chatbots into websites. It aims to enhance user experience, improve customer support, and enable businesses to easily engage with their customers. RevoChat offers personalized assistance, intuitive chat interface, and different pricing plans to cater to diverse business needs.', NULL, '["AI Agents","E-commerce","Customer Support"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('9f6d6a88-46f6-48c3-85d0-8ae33faddb6f', '2024-04-12T15:29:21.676Z', 'Revoldiv', 'https://revoldiv.com/', 'images/revoldiv.webp', 'images/revoldiv-icon.webp', '{"what":"Revoldiv is an AI tool that converts audio/video files to text with high accuracy, allowing users to easily edit and export the transcript in various formats. It also offers features like filler word removal, keyword search, and chapter creation.","who":"Revoldiv is designed for content creators, podcasters, video editors, and anyone who needs accurate transcription and editing capabilities for their audio/video files.","best_features":["High accuracy transcription of audio/video files","Easy editing and exporting of transcripts in various formats","Advanced features like filler word removal and chapter creation"]}', 'Revoldiv is an AI tool that converts audio/video files to text with high accuracy, allowing users to easily edit and export the transcript in various formats. It includes features such as filler word removal, keyword search, and chapter creation. The tool is currently supported on Chrome and Firefox and can handle media files under two hours long.', NULL, '["Transcription","Audio Editing","Video Editing"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('b6bbc962-a34d-4517-bac2-18a5d5363c83', '2024-04-12T15:29:21.676Z', 'Revrite', 'https://www.revrite.ai/', 'images/revrite.webp', 'images/revrite-icon.webp', '{"what":"Revrite.ai is an online paraphrasing tool that uses artificial intelligence to rephrase text in over 100 languages, including English, German, Spanish, Chinese, Russian, and French, to make it more readable, fluent, and simple.","who":"Revrite.ai is for anyone looking to improve the quality and readability of their written content, including professionals, students, researchers, and individuals who want to enhance their messages for work, academic, or personal projects.","best_features":["Supports paraphrasing in over 100 languages, providing a wide range of language options for users.","Understands entire paragraphs for better quality output compared to paraphrasing based on individual sentences.","Rich text editing support allows users to copy and paste entire documents for quick and easy rewriting."],"pricing":""}', 'Revrite.ai is an online paraphrasing tool that uses artificial intelligence to rephrase text in multiple languages, including English, German, Spanish, Chinese, Russian, and French. It can handle various types of content, such as emails, WhatsApp messages, Instagram posts, and research articles, and rewrite them to enhance the overall message quality. The tool provides rich text editing support, works on mobile devices, and allows users to customize the paraphrasing according to their preferences. Revrite.ai is ideal for professionals, students, researchers, and individuals looking to improve the readability and quality of their written content.', NULL, '["AI Solutions"]');
