INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('9c81ec1d-1f5b-42fb-9515-ee93cb3807fb', '2024-04-12T15:29:21.688Z', 'Rooms', 'https://rooms.xyz/', 'images/rooms.webp', 'images/rooms-icon.webp', '{"what":"Rooms.xyz is a web-based tool that allows users to create and remix interactive rooms in their browser. Users can customize rooms by choosing from hundreds of items, view source code to make changes, and share room links for playback on desktop and iOS devices.","who":"This product is ideal for individuals looking to design and personalize interactive rooms for various purposes such as home improvement projects, 3D modeling, advertising campaigns, and virtual events.","best_features":["Create and browse interactive rooms on web and iOS.","Choose from thousands of items to add to your room.","View source code of any item and customize how it works."],"pricing":[]}', 'Rooms.xyz is a versatile web-based tool that empowers users to create and customize interactive rooms with a wide range of features. From selecting items to editing existing elements and sharing room links, users have the flexibility to design rooms for different purposes. The ability to view source code and make changes adds a layer of customization, making it a valuable tool for various projects.', NULL, '["3D Modeling","Advertising","AI Agents"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('f946f61b-f435-4a07-9fec-620238405c41', '2024-04-12T15:29:21.688Z', 'Root Vocab', 'https://rootvocab.com/', 'images/root-vocab.webp', 'images/root-vocab-icon.webp', '{"what":"Root Vocab is an AI-powered vocabulary learning platform that offers a comprehensive set of tools and features to assist users in mastering new words and concepts.","who":"This product is designed for individuals looking to enhance their vocabulary skills, whether for academic, professional, or personal development purposes.","best_features":["AI language assistant named Artemis for personalized learning experience","Spaced Repetition algorithm for prioritizing practice on difficult words","Interactive language-learning games for engaging and effective learning"],"pricing":[{"plan":"Free plan available","price":0,"billing_cycle":"N/A","original_price":0,"discount":"N/A","features":["Access to flashcards, word maps, quizzes, and AI examples","Personalized vocabulary generation and statistics tracking","Gamified language learning experience with AI-powered games"]}]}', 'Root Vocab is an AI platform that assists in vocabulary learning and mastery through a blend of AI practice algorithms, interactive language-learning games, and informative metrics. The platform features an AI language assistant named Artemis, personalized learning experiences, and a gamified approach to language learning. Users can start for free and upgrade to a plan that suits their needs.', NULL, '["Language Learning","AI Technology","Gamified Learning"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('5239d80e-b339-4c6f-9204-1fa650f388f4', '2024-04-12T15:29:21.688Z', 'Ropes AI', 'https://www.ropes.ai/', 'images/ropes-ai.webp', 'images/ropes-ai-icon.webp', '{"what":"Ropes AI is an AI-powered tool designed to improve the coding assessment process for organizations by focusing on skill tracking over test case validations. It offers detailed scorecards, customization options, anti-cheating security, and seamless integration with recruitment workflows.","who":"Ropes AI is for early-stage startups looking to establish effective interview processes, fast-growing teams in need of a reliable interview setup, and enterprises seeking to identify top engineering talents.","best_features":["Detailed scorecards that provide comprehensive insights into candidate capabilities","Customizable coding challenges unique to each business","Anti-cheating security mechanism to ensure assessment integrity"],"pricing":[]}', 'Ropes AI is an AI-powered tool aimed at improving the coding assessment process for organizations of various sizes. It provides a unique approach to screening engineers as part of the recruitment or performance evaluation process, focusing on skill tracking and detailed scorecards. The tool offers customization, anti-cheating security, and seamless integration with recruitment workflows to expand the hiring funnel for engineering talents.', NULL, '["Recruitment","Coding Assessment","Skill Tracking"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('e1a362a5-74a9-4de2-bc97-6d31eb91459d', '2024-04-12T15:29:21.688Z', 'ROQ', 'https://www.roq.ai/', 'images/roq.webp', 'images/roq-icon.webp', '{"what":"ROQ.ai is a tool that enables users to quickly build and deploy SaaS web applications using prompts and plain English descriptions.","who":"ROQ.ai is designed for developers and individuals looking to accelerate their web app development process by leveraging pre-integrated features and AI assistance.","best_features":["AI-assisted data modeling for rapid and robust schema building","Secure user authentication with social logins and 2FA out of the box","Organizations, roles, and permissions management for easy access control"],"pricing":[]}', 'ROQ.ai is a tool that allows users to describe their app in simple terms and receive a fully deployed web app with source code, including data model, APIs, forms, authentication, roles, permissions, and notifications. Users can launch their SaaS application weeks earlier with more features from day one.', NULL, '["AI Tools","No-code Development","Web Development"]');
