INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('9155d1c6-a79c-4f10-86e3-3368097bc28b', '2024-04-12T15:29:21.690Z', 'Rude Karma', 'https://www.rude-karma.com/', 'images/rude-karma.webp', 'images/rude-karma-icon', '{"what":"Rude Karma is a unique horoscope app that provides astrology readings with a sassy tone, offering daily horoscope updates and interactive features for users to enjoy.","who":"This app is designed for astrology enthusiasts who enjoy a fun and unconventional approach to horoscope readings, as well as individuals looking for a fresh and entertaining way to engage with astrology.","best_features":["Daily horoscope updates with a sassy tone for unapologetic reality checks.","Option to choose between ''nice'' or ''rude'' settings for a personalized experience.","Astral navigation feature to explore current cosmic transits and lunar events."],"pricing":[]}', 'Rude Karma is a distinctive horoscope app that delivers astrology readings with a sassy tone. Users can choose between ''nice'' or ''rude'' settings for their horoscope updates and pass the ''sass'' along to friends and family. The app also includes an astral navigation feature to explore life''s current cosmic transits and lunar events. Available for download on iOS and Android platforms, Rude Karma offers a fresh take on traditional horoscope apps.', NULL, '["Astrology","Mobile App"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('97e18936-456c-463d-9526-cdfc6a95f05d', '2024-04-12T15:29:21.690Z', 'Rumi', 'https://rumi.ai/', 'images/rumi.webp', 'images/rumi-icon.webp', '{"what":"Rumi.ai is an AI-powered tool designed to enhance meeting productivity by providing AI-synced notes and tasks. It converts meetings into easy-to-follow notes and logs actions for next steps, aiming to streamline workflows and enhance action tracking.","who":"Rumi.ai is designed for professionals, teams, and organizations looking to improve meeting efficiency, streamline workflows, and enhance collaboration.","best_features":["AI-powered note-taking and task logging for seamless action tracking","Integration with Google Calendar for easy meeting scheduling and syncing","Interactive summaries, insights, and action items during meetings for focused discussions"],"pricing":""}', 'Rumi.ai is an AI-powered tool that enhances meeting productivity by providing AI-synced notes and tasks. It streamlines workflows, enhances action tracking, and offers features like interactive summaries, insights, and action items during meetings. With integration into Google Calendar, users can easily schedule and sync meetings, while post-meeting capabilities include replay, review, and relay functionalities. Rumi.ai also offers privacy features like ''Off the Record'' mode and turn-based mode for orderly meetings, making it a comprehensive tool for professionals and teams.', NULL, '["Meeting Assistance","AI Tools","Communication"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('06572817-e788-4916-b0fd-d5703d978cb4', '2024-04-12T15:29:21.690Z', 'Run Recommender', 'https://takethisandrun.com/', 'images/run-recommender.webp', 'images/run-recommender-icon.webp', '{"what":"The Run Recommender is a user-friendly AI tool designed to assist individuals in finding the perfect running shoes by inputting specific details and receiving personalized recommendations.","who":"This product is for runners of all levels who are looking for guidance in selecting the right running shoes based on their unique needs and preferences.","best_features":["Personalized shoe recommendations based on user input","Time-saving by avoiding the need to sift through numerous reviews","Cost-effective starting point to find running shoes that fit budget and body"],"pricing":[]}', 'The Run Recommender is a tool that uses AI to suggest running shoe options based on user input such as shoe width, age, weight, and running pace. It provides personalized recommendations, explanations for the choices, and additional insights to help users make informed decisions. By using this tool, individuals can save time, avoid overwhelming options, and find running shoes that fit their budget and needs.', NULL, '["AI Solutions","Health and Wellness"]');
