INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('9a0e4fcb-eaf7-4d5b-b3a9-25c2b4a86cf9', '2024-04-12T15:29:21.696Z', 'Sanctum', 'https://sanctum.ai/', 'images/sanctum.webp', 'images/sanctum-icon.svg', '{"what":"Sanctum is a private and local AI assistant that allows users to run and interact with full-featured open-source Large Language Models (LLMs) directly on their device. It prioritizes privacy by encrypting all data and ensuring it never leaves the user''s device.","who":"Sanctum is designed for individuals who value privacy and control over their data, as well as those who require AI-based services for tasks such as text interpretation, PDF interaction, and data storage.","best_features":["Privacy-first approach with on-device encryption and processing","AI matching engine for easy switching between open-source models","Secure local repository (Sanctum Vault) for encrypted data storage"]}', 'Sanctum is a private and local AI assistant that enables users to run and interact with open-source Large Language Models (LLMs) on their device. It prioritizes privacy by encrypting all data and ensuring it never leaves the user''s device. The tool also features an AI matching engine for model selection, PDF interaction capabilities, and a secure local data storage solution.', NULL, '["Privacy-focused","Data Security","AI Assistance"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('6f54381b-13e9-4b10-aad9-83781c1a34ca', '2024-04-12T15:29:21.696Z', 'SaneBox', 'https://www.sanebox.com/', 'images/sanebox.webp', 'images/sanebox-icon.webp', '{"what":"SaneBox is an email management tool that utilizes artificial intelligence to assist users in organizing their inbox effectively and reducing email overload.","who":"SaneBox is designed for individuals and businesses looking to enhance productivity by streamlining their email management process.","best_features":["Automatically identifies important emails to prioritize attention and stay focused.","Offers trainable features for customized email sorting, such as special filters and contact training.","Provides daily updates about email activity and offers features like email deep cleaning and email organization options."],"pricing":[]}', 'SaneBox is an email management tool that aims to enhance productivity by reducing the time spent on email management. It employs artificial intelligence (A.I.) to assist users in organizing their inbox effectively. The tool automatically identifies important emails, allowing users to prioritize their attention and stay focused. Users can also take advantage of the tool''s trainable features, such as special filters and contact training, to customize the email sorting process. SaneBox provides daily updates about email activity, ensuring that users stay informed and do not miss any important emails.', NULL, '["Productivity Tools","AI Tools","Artificial Intelligence"]');
