INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('60ae6200-6553-461f-8931-1add7c63f0af', '2024-04-12T15:29:21.709Z', 'SearchGPT', 'https://searchgpt.ai', 'images/searchgpt.webp', 'images/searchgpt-icon.webp', '{"what":"SearchGPT''s Spreadsheet Generator is a tool designed to simplify the process of creating spreadsheets based on specific instructions. It automates the spreadsheet creation process, saving time and effort for users.","who":"This product is ideal for individuals or businesses who frequently need to create spreadsheets for various purposes, such as data analysis, project management, financial tracking, and more.","best_features":["User-friendly interface for easy input and generation of spreadsheets","Ability to create both basic tables and more complex spreadsheets","Quickly assists users in getting the job done efficiently"],"pricing":[]}', 'SearchGPT''s Spreadsheet Generator is a handy tool that simplifies the process of creating spreadsheets based on specific instructions. Users can enter their prompt and click generate to have a spreadsheet generated for them. Whether they need a basic table or a more complicated spreadsheet, this tool can quickly assist in getting the job done. Save time and effort by automating the spreadsheet creation process with SearchGPT''s user-friendly interface.', NULL, '["Automation","Data Analysis","Spreadsheet Management"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('1d09ec0e-57eb-4354-acce-f354166b0a68', '2024-04-12T15:29:21.710Z', 'SearchMyDocs.ai', 'https://searchmydocs.ai/', 'images/searchmydocs-ai.webp', 'images/searchmydocs-ai-icon.webp', '{"what":"SearchMyDocs.ai is an AI-powered tool that turns your private document library into an intelligent searchable entity, providing a more efficient way of finding specific information from extensive document collections.","who":"This product is designed for individuals and businesses who deal with large amounts of documents and need a streamlined way to search for and retrieve specific information quickly.","best_features":["AI-powered precision in information retrieval, allowing users to ask complex questions and receive contextually accurate answers","Privacy and security features such as data encryption and role-based access control","Support for multiple file formats including PDF, Word, Excel, and image formats"]}', 'SearchMyDocs.ai is an AI-powered tool that helps users search, chat, and ask questions about the content of their documents. It uses deep learning models and OCR capabilities to extract text and key-value pairs from documents, enabling users to find specific information quickly and efficiently. The tool prioritizes user privacy and data security, with data encryption and role-based access control. Users can choose from different pricing plans based on their storage and query needs.', NULL, '["Document Management","Search Engine","Privacy & Security"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('544d3e80-f01f-4b19-8297-4388bcb34f77', '2024-04-12T15:29:21.710Z', 'SearchPlus', 'https://www.searchplus.ai/', 'images/searchplus.webp', 'images/searchplus-icon.svg', '{"what":"Search+ is an AI-powered PDF reader that enables users to interact with their documents through conversation, providing instant and precise information in response to queries.","who":"Search+ is designed for individuals, teams, and organizations looking to streamline their document analysis process and extract insights efficiently.","best_features":["AI-powered conversation-led interaction with PDF documents","Support for multiple languages and secure data encryption","Customizable integrations and 24/7 customer support"]}', 'Search+ is an AI-powered PDF reader that revolutionizes how users interact with their documents by enabling conversation-led queries for instant and precise information retrieval. It supports multiple languages, secure data encryption, and customizable integrations, making it a versatile tool for individuals, teams, and organizations.', NULL, '["AI Tools","Document Management","Data Analysis"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('3e073bf0-36a7-4b18-9ca0-4dede270518d', '2024-04-12T15:29:21.710Z', 'SEC Insights', 'https://www.secinsights.ai/', 'images/sec-insights.webp', 'images/sec-insights-icon.svg', '{"what":"SEC Insights is an AI tool designed to enhance an organization''s Business Intelligence by simplifying the analysis of complex financial documents such as 10-Ks and 10-Qs.","who":"SEC Insights is for organizations looking to streamline the process of analyzing multifaceted financial documents and gain comprehensive insights for making informed decisions.","best_features":["Effortlessly analyze complex financial documents like 10-Ks and 10-Qs","Extract meaningful insights from inquiries across multiple documents","Navigate through dense financial information with precision by highlighting paragraph-level citations"],"pricing":[]}', 'SEC Insights is an AI tool that simplifies the analysis of complex financial documents such as 10-Ks and 10-Qs. It allows users to explore and analyze up to 10 documents at a time, providing comprehensive insights and facilitating deep comparisons. The tool highlights paragraph-level citations to enhance clarity and comprehension, empowering organizations to make informed decisions based on a better understanding of financial data.', NULL, '["Business Intelligence","AI Tool","Data Analysis"]');
