INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('394f3aff-f61b-4319-a255-a1e8197b6ac6', '2024-04-12T15:29:21.711Z', 'Secur3D', 'https://secur3d.ai/', 'images/secur3d.webp', 'images/secur3d-icon.webp', '{"what":"Secur3D is an AI-powered tool designed to analyze, moderate, and authenticate 3D content, safeguarding intellectual property and copyrighted works from theft and infringement.","who":"Secur3D is for creators, designers, artists, and businesses who deal with 3D content and want to protect their intellectual property and copyrights from theft and misuse.","best_features":["Geometry Analysis for quick and accurate comparison of asset libraries","Texture Analysis to identify plagiarism and art theft in 2D texture maps","Explicit Content Detection using computer vision to find and classify inappropriate content"]}', 'Secur3D is an AI-powered tool that analyzes, moderates, and authenticates 3D content to protect intellectual property and copyrighted works. It offers features like Geometry Analysis, Texture Analysis, and Explicit Content Detection to ensure accuracy and efficiency in content moderation. By detecting brand infringements and assisting in asset categorization, Secur3D helps create safe ecosystems for creators and businesses.', NULL, '["Content Moderation","Intellectual Property Protection","3D Content Analysis"]');
