INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('35ab084e-601d-472d-82a8-46de0fdac42f', '2024-04-12T15:29:21.724Z', 'ShoppingBuddy', 'https://shopping-buddy.netlify.app/', 'images/shoppingbuddy.webp', 'images/shoppingbuddy-icon.webp', '{"what":"ShoppingBuddy is an AI-integrated shopping assistant that aims to revolutionize the shopping experience by providing users with exclusive early access to the future of shopping.","who":"ShoppingBuddy is for anyone who wants to streamline their shopping experience, find products quickly and easily, and receive personalized recommendations.","best_features":["Product discovery: ShoppingBuddy helps users find the exact product they are looking for quickly and easily.","Chat functionality: The tool offers a user-friendly chat interface, making interacting with it effortless and intuitive.","Effortless purchasing: ShoppingBuddy seamlessly integrates with popular online stores and filters to display the most relevant products."],"pricing":""}', 'ShoppingBuddy is an AI-integrated shopping assistant that streamlines the shopping process by providing quick and easy product discovery, seamless integration with popular online stores, filtering to display the most relevant products, and a user-friendly chat interface.', NULL, '["E-commerce","Chat Solutions","Productivity Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('21a8ea64-8626-4c2b-9411-96e931e25046', '2024-04-12T15:29:21.724Z', 'Shortbread', 'https://shortbread.ai/', 'images/shortbread.webp', 'images/shortbread-icon.webp', '{"what":"Shortbread is an AI-powered tool that enables users to create comics, webtoon, and manga in just minutes. It offers a user-friendly interface for storytellers to bring their ideas to life by transforming concepts into visually captivating pages.","who":"Shortbread is for storytellers, comic creators, webtoon artists, and manga enthusiasts who want to streamline the creative process and bring their stories to life with the help of AI technology.","best_features":["AI-powered engine for quick and intuitive comic creation","Unparalleled control over scenes, character poses, and design elements","Automatic generation of pages based on user inputs"]}', 'Shortbread is an AI-powered tool that simplifies the creation of comics, webtoon, and manga. It offers a range of features that give users control over their creations, from manipulating scenes to customizing design elements. The tool uses AI to generate pages based on user inputs, making the creative process quick and intuitive. Shortbread is designed to help storytellers bring their ideas to life with visually stunning results.', NULL, '["Art Generation","AI Tools","Story Generation"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('ce5ef59c-4978-4467-92da-112c6cd8a979', '2024-04-12T15:29:21.725Z', 'ShortCast', 'https://shortcast.ai/', 'images/shortcast.webp', 'images/shortcast-icon.svg', '{"what":"ShortCast is an AI tool designed to quickly summarize long YouTube videos and podcasts, providing users with concise and coherent text summaries. It also offers audio summaries and a Deep Dive Assistant feature for in-depth content exploration.","who":"ShortCast is ideal for individuals who want to save time by efficiently understanding the key points of lengthy audio and video content. It is beneficial for students, professionals, content creators, and anyone looking to consume information more effectively.","best_features":["Advanced natural language processing for accurate summarization","Ability to condense a 45-minute video or podcast into a 3-minute summary","Deep Dive Assistant for detailed content exploration"],"pricing":[{"plan":"Free Trial","price":0,"billing_cycle":"Monthly","original_price":0,"discount":"N/A","features":["Access to all platform features","Limited to one free trial per month","No credit card required"]}]}', 'ShortCast is an AI tool that offers quick and efficient summarization of long YouTube videos and podcasts. Powered by advanced natural language processing, it extracts key points from lengthy audio and video content and condenses it into concise and coherent text summaries. Users can understand the essence of a 45-minute podcast or video in just 3 minutes. In addition to text summaries, ShortCast also provides audio summaries and a Deep Dive Assistant feature for detailed content exploration. The platform offers a free trial each month without the need for a credit card.', NULL, '["Summarization","Audio Editing","Content Creation"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('f83d23cf-246a-4f7e-b3db-f5d530f2e0b2', '2024-04-12T15:29:21.726Z', 'ShortlistIQ', 'https://www.shortlistiq.com/', 'images/shortlistiq.webp', 'images/shortlistiq-icon.webp', '{"what":"ShortlistIQ is an AI-powered tool designed to assist with recruiting tasks, particularly with the screening of candidates. It automates the first-round interview process using conversational AI.","who":"ShortlistIQ is designed for HR professionals and organizations looking to streamline their recruitment process and improve candidate screening efficiency.","best_features":["Customizable AI assistants that reflect unique organizational culture","Strategic questioning to assess candidate competence","Human-like interactions for personalized and engaging interviews"],"pricing":[]}', 'ShortlistIQ is an AI-powered tool that automates the screening of candidates by conducting first-round interviews using conversational AI. It offers customizable AI assistants, strategic questioning, and human-like interactions to provide a fair and engaging experience for both candidates and recruiters.', NULL, '["Recruitment","HR Technology"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('9683b362-52b9-40c8-a31e-8d087b23f148', '2024-04-12T15:29:21.726Z', 'Shortmake', 'https://app.shortmake.com/', 'images/shortmake.webp', 'images/shortmake-icon.webp', '{"what":"ShortMake is an AI-powered tool that simplifies the video creation process by transforming ideas into viral videos quickly and effortlessly.","who":"ShortMake is for content creators, social media influencers, marketers, and anyone looking to create engaging video content without the need for technical expertise.","best_features":["AI-powered editing for quick and unique video creation","Customization options for voiceover, music, script, and illustrations","High-quality AI-generated voices for professional touch"],"pricing":[{"plan":"Free","price":0,"billing_cycle":"Monthly","original_price":0,"discount":"N/A","features":["AI-powered video creation","Customization options","High-quality AI-generated voices"]},{"plan":"Premium","price":9.99,"billing_cycle":"Monthly","original_price":12.99,"discount":"23% off","features":["All Free plan features","Priority support","Access to premium illustrations"]}]}', 'ShortMake is an AI-powered tool that enables users to create viral videos effortlessly. It uses artificial intelligence to generate scripts, voice-overs, select images or video illustrations, add subtitles, and edit everything into a single video suitable for platforms like TikTok and YouTube Shorts. Users can customize their videos by personalizing the voiceover, choosing background music, editing the AI-generated script, and selecting illustrations for the final video montage. ShortMake offers high-quality AI-generated voices and ensures a professional touch to the videos. The process of generating a video with ShortMake is quick, taking less than 8 minutes from start to finish.', NULL, '["AI Tools","Video Editing","Social Media"]');
