INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('b2e9a396-9889-47a7-be17-ec55fe121d75', '2024-04-12T15:29:21.727Z', 'Show by Animaker', 'https://www.getshow.io/', 'images/show-by-animaker.webp', 'images/show-by-animaker-icon.svg', '{"what":"Show by Animaker is an AI-powered email marketing tool designed to help users increase email conversion rates through effective email campaigns.","who":"This product is for businesses looking to enhance their outbound marketing efforts and improve their email marketing and sales outreach strategies.","best_features":["AI-powered content creation and audience segmentation","Personalized campaigns on dynamic rule engines","In-built hard stops feature for deliverability alerts"],"pricing":[]}', 'Show by Animaker is an AI-powered email marketing tool that leverages AI technology for custom content creation, audience segmentation, and personalized campaigns. It offers features for setting up lead capture mechanisms, meeting scheduling, and feedback collection within emails to enhance engagement. The platform also includes in-built hard stops for deliverability alerts and protection of the domain. Show by Animaker is ideal for businesses seeking to optimize their email marketing and sales outreach efforts.', NULL, '["AI-powered Tools","Marketing Automation","Engagement Platforms"]');
