INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('1471e03b-a2d4-4148-9852-60cc75595732', '2024-04-12T15:29:21.728Z', 'Shy Editor', 'https://www.shyeditor.com/', 'images/shy-editor.webp', 'images/shy-editor-icon.webp', '{"what":"Shy Editor is an AI-empowered writing environment that assists various types of writers in improving writing speed, quality, productivity, and confidence.","who":"This product is designed for novelists, non-fiction writers, students, academics, bloggers, content creators, and journalists.","best_features":["AI assistant for feedback and brainstorming","Markdown editor for versatile formatting","Knowledge base for research organization"],"pricing":[{"plan":"Basic","price":0,"billing_cycle":"Monthly","original_price":0,"discount":"Free","features":["20 AI Assistant credits per month","100MB storage","Unlimited projects for individuals"]},{"plan":"Pro","price":10,"billing_cycle":"Monthly","original_price":10,"discount":"","features":["200 AI Assistant credits per month","5GB storage","Export to pdf and docx"]}]}', 'Shy Editor, formerly known as Pencil, is an AI-empowered writing environment that enhances writer productivity, quality, and confidence. It offers features like an AI assistant, Markdown editor, knowledge base, and more to cater to various types of writers.', NULL, '["AI Tools","Productivity Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('61b61af4-7005-4187-9320-cc9fab6598f5', '2024-04-12T15:29:21.728Z', 'Sibylia', 'https://sibylia.io/en/', 'images/sibylia.webp', 'images/sibylia-icon.webp', '{"what":"Sibylia is an advanced AI-powered solution that automatically generates captivating audio descriptions for video content, making it accessible to a wider audience.","who":"Sibylia is designed for social media creators, content producers, visually impaired individuals, deaf and hard-of-hearing individuals, and anyone looking to optimize their content for social media.","best_features":["Automatically generate audio descriptions for video content","Generate text descriptions using different AI models","Optimize content for social media platforms like TikTok, Twitch, and YouTube"]}', 'Sibylia is an AI-powered solution that makes content accessible by automatically generating audio descriptions for video content. It enables visually impaired individuals to access content in a meaningful way and provides text-to-speech descriptions for the deaf and hard-of-hearing. Sibylia also allows users to generate descriptions in different languages and optimize content for social media platforms. Whether you want to enhance accessibility, reach a wider audience, or learn new languages, Sibylia''s advanced AI models have got you covered.', NULL, '["Content Creation","Accessibility","Social Media"]');
