INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('e6fed4f3-05a2-41de-9f96-0fe9701b4783', '2024-04-12T15:29:21.728Z', 'Sidekick by Jigso', 'https://www.jigso.io/sidekick', 'images/sidekick-by-jigso.webp', 'images/sidekick-by-jigso-icon.webp', '{"what":"Sidekick by Jigso is an AI-powered Slackbot that serves as a personal business assistant, helping users track important topics, set reminders, query documents, and summarize conversations in Slack. It also provides free access to ChatGPT 4.0 for content generation.","who":"Sidekick is designed for professionals across various industries who use Slack for communication and task management.","best_features":["AI-powered assistance for tracking and managing tasks","Integration with ChatGPT 4.0 for content generation","Summarization of conversations and direct messages in Slack"],"pricing":[]}', 'Sidekick by Jigso is an AI-powered Slackbot that acts as a personal business assistant, offering features such as tracking important topics, setting reminders, querying documents, and summarizing conversations in Slack. Users also get free access to ChatGPT 4.0 for content generation.', NULL, '["Chat Solutions","Productivity Tools","AI Services"]');
