INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('7728618c-5294-4184-9dec-388dbe6240b5', '2024-04-12T15:29:21.733Z', 'SimplyPut', 'https://simplyput.ai/', 'images/simplyput.webp', 'images/simplyput-icon.webp', '{"what":"SimplyPut is a data analytics tool built on Artificial Intelligence to aid companies interact with data more efficiently and effectively. It translates data into an understandable format, enabling timely decision-making.","who":"SimplyPut is designed for data teams and end users who want to manage company-specific datasets and derive actionable insights.","best_features":["Instant insights and precise answers to questions in natural language","User-friendly interface for intuitive user experience","Real-time insights and analytics integration with Slack"],"pricing":[]}', 'SimplyPut is a data analytics tool powered by AI that helps companies interact with data effectively. It translates complex data into understandable insights, enabling informed decision-making. Users can ask questions in their own language and receive precise answers instantly. The tool promotes understanding by demonstrating its reasoning process and can be integrated with Slack for real-time insights.', NULL, '["Artificial Intelligence","AI Tools","AI Solutions"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('4d4887c8-fcd0-4bba-a3ac-473a6f79284a', '2024-04-12T15:29:21.733Z', 'Simulai', 'https://www.simulai.co/', 'images/simulai.webp', 'images/simulai-icon.webp', '{"what":"Simulai is an AI-powered tool that allows users to create interactive conversational forms that mimic natural conversations. It offers a minimalist design inspired by Notion, making it easy to build forms that engage users. Users can capture contact information, numbers, rankings, files, dates & times, and add conditional logic. Simulai is open-source and privacy-friendly, with options for self-hosting or using free cloud hosting services.","who":"Simulai is designed for businesses, marketers, researchers, and individuals who want to create engaging and interactive surveys or forms. It is suitable for companies looking to gather insights, engage users, and improve data collection processes.","best_features":["Minimalistic design inspired by Notion for easy form creation","Ability to capture various types of data including contact info, numbers, rankings, and files","Open-source project with options for self-hosting or free cloud hosting services"]}', 'Simulai is an AI tool that enables users to create interactive conversational forms for gathering data and engaging users. With a minimalist design inspired by Notion, users can capture a wide range of data types and add conditional logic to their forms. The tool is open-source, privacy-friendly, and offers options for self-hosting or free cloud hosting services.', NULL, '["Data Collection","Form Builder","Privacy-Friendly"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('56fdd5bd-3221-40cc-b1c9-778612092b05', '2024-04-12T15:29:21.733Z', 'SingleAPI', 'https://singleapi.co/', 'images/singleapi.webp', 'images/singleapi-icon.webp', '{"what":"SingleAPI is a tool that allows users to convert any website into an API in seconds. It is powered by GPT-4 and uses web navigation and data extraction techniques to convert website content into JSON format.","who":"SingleAPI is designed for individuals, businesses, and big companies looking to easily extract data from websites, enrich datasets, and generate APIs without the need for manual coding or selector writing.","best_features":["Powerful data scraping engine that extracts data from websites without the need for selectors.","Built-in data enrichment tools to enhance the quality and completeness of datasets.","Support for multiple data formats, real-time data delivery via webhooks, and automatic search engine scraping."]}', 'SingleAPI is a powerful and user-friendly tool that converts websites into APIs, offering features such as data scraping, data enrichment, and automatic API generation. It provides flexibility in data formats, proxy management, and search engine scraping, making it suitable for a wide range of use cases.', NULL, '["API Generation","Data Scraping","Data Enrichment"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('ba7053c1-4452-4736-8682-cd21234cf697', '2024-04-12T15:29:21.733Z', 'SINT', 'https://www.usesint.com/', 'images/sint.webp', 'images/sint-icon.svg', '{"what":"SINT is an AI tool focused on revolutionizing the hiring process by promoting merit and potential-based recruitment. It utilizes advanced AI technology to offer several key features.","who":"SINT is designed for companies of all sizes and industries looking to streamline their hiring process, make data-driven hiring decisions, eliminate bias, and find top talent efficiently.","best_features":["AI-driven CV evaluations for fair and objective analysis of candidate qualifications and experiences","Innovative Interview Simulation feature for assessing candidate skills and responses under pressure","Performance dashboard for real-time analysis of candidate performances and data-backed insights for hiring decisions"],"pricing":[]}', 'SINT is an AI tool that aims to transform the recruitment process by utilizing AI technology to provide fair and objective candidate evaluations. It offers features such as AI-driven CV evaluations, Interview Simulation, and a performance dashboard for data-driven insights. SINT helps companies make informed hiring decisions based on merit and potential, while also promoting a more unbiased evaluation process.', NULL, '["Recruitment","Hiring","Artificial Intelligence"]');
