INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('72cce074-91cf-4c57-a925-5da9ba27473a', '2024-04-12T15:29:21.734Z', 'Siteefy business names', 'https://siteefy.com/ai-business-idea-generator/', 'images/siteefy-business-names.webp', 'images/siteefy-business-names-icon.webp', '{"what":"The AI Business Idea Generator by Siteefy is a tool designed to assist entrepreneurs in finding and validating their next business idea.","who":"This product is for entrepreneurs who are looking for personalized business ideas that align with their skills, passions, and market demand.","best_features":["Personalized Idea Generation tailored to user''s interests, skills, and goals","Real-Time Market Insights based on market analysis","Guidance for Idea Validation with actionable insights and suggested steps"],"pricing":[]}', 'The AI Business Idea Generator by Siteefy is an AI-powered tool that helps entrepreneurs find and validate their next business idea. It guides users through a three-step process of defining their goals, receiving personalized business ideas, and validating those ideas with actionable insights. The tool aims to empower entrepreneurs by leveraging AI technology to provide tailored, market-driven business ideas.', NULL, '["Entrepreneurship","AI Technology"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('9bf1b25c-9108-4227-a519-c7a9539db760', '2024-04-12T15:29:21.734Z', 'Siteefy content checker', 'https://siteefy.com/ai-content-checker/', 'images/siteefy-content-checker.webp', 'images/siteefy-content-checker-icon.webp', '{"what":"The AI Content Checker is a browser extension tool developed by Siteefy that aims to enhance the publishing process by ensuring rapid and error-free content creation. It allows users to review and summarize web pages in multiple languages, ask specific questions about the page content, and receive relevant answers from the AI.","who":"The AI Content Checker is designed for individuals working in various roles, including SEO specialists, digital marketers, and content creators who want to streamline the content creation process and make it easier and more efficient.","best_features":["Review any web page with AI and receive tips on improving content quality.","Ask specific questions about a web page and get relevant answers from the AI.","Highlight HTML headings, links opening in new tabs, spacing issues, and broken links for better page structure insights."],"pricing":[]}', 'The AI Content Checker by Siteefy is a browser extension tool that helps users enhance the publishing process by ensuring rapid and error-free content creation. It allows users to review and summarize web pages in multiple languages, ask specific questions about the content, and receive relevant answers from the AI. The tool highlights HTML headings, links opening in new tabs, spacing issues, and broken links to provide insights into the page structure. With features like content review, AI question answering, and page structure analysis, the AI Content Checker aims to make content creation easier and more efficient.', NULL, '["Content Creation","SEO","Digital Marketing"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('98ab6633-7d4a-4a37-944c-c73c36b0dafa', '2024-04-12T15:29:21.735Z', 'SiteExplainer', 'https://www.siteexplainer.com/', 'images/siteexplainer.webp', 'images/siteexplainer-icon.webp', '{"what":"SiteExplainer is an AI-powered web app that simplifies complex website content into an easily understandable format through quick and accurate summarization in seconds.","who":"SiteExplainer is for visitors unfamiliar with technical language and looking to identify relevant information quickly on websites.","best_features":["Quick and accurate summarization of website content","Eliminates corporate jargon for easier understanding","Compatible with both desktop and mobile devices"],"pricing":[]}', 'SiteExplainer is an AI-powered web app that simplifies complex website content into an easily understandable format through quick and accurate summarization in seconds. It eliminates corporate jargon and provides summaries of main ideas and key points. The tool is particularly useful for visitors unfamiliar with technical language and looking to identify relevant information quickly. It is compatible with both desktop and mobile devices and is completely free to use.', NULL, '["Summarization"]');
