INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('71cb8f56-f04b-4421-8ad3-63bc0c04581e', '2024-04-12T15:29:21.736Z', 'Siwalu', 'https://siwalusoftware.com/', 'images/siwalu.webp', 'images/siwalu-icon.webp', '{"what":"Siwalu is an AI-based animal recognition tool that helps pet owners identify and learn about the breed and characteristics of their pets quickly and accurately.","who":"Siwalu is designed for pet owners who want to learn more about their pets'' breeds and traits without the need for costly DNA analysis.","best_features":["Accurate breed identification for dogs, cats, and horses within seconds","Social feed feature for users to share their pet-related content","Partnership with Snapchat for animal recognition through the app''s camera"],"pricing":[]}', 'Siwalu is an AI-based animal recognition tool that can identify and scan animals, providing pet owners with specific information about their pets'' breeds and characteristics. The tool is available on the app store and offers different scanners for dogs, cats, and horses. Users can quickly learn about their pets without the need for costly DNA analysis. Siwalu also features a social feed for users to share their pet-related content and has partnered with Snapchat to bring animal recognition to the app''s camera.', NULL, '["AI Services","Technology"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('c743085e-7f07-4b3b-87d0-2502bcbd1cca', '2024-04-12T15:29:21.737Z', 'Sixty', 'https://sixty.app/', 'images/sixty.webp', 'images/sixty-icon.webp', '{"what":"Sixty AI is a time-saving tool that leverages artificial intelligence to automate tasks, prioritize important communications, and bring clarity to users'' schedules so they can focus on what truly matters.","who":"Sixty AI is designed for individuals who want to reclaim their time by automating tasks that can be handled by AI, stay organized, and prioritize important communications.","best_features":["Automates tasks such as clearing inboxes, scheduling, drafting agendas, and preparing briefings","Customizable snapshots of daily, weekly, and weekend schedules for organization","Filters out noise in email communications and prioritizes important messages"]}', 'Sixty AI is a tool designed to help users reclaim their time by automating tasks that can easily be handled by artificial intelligence. It focuses on eliminating time wasted on tasks such as clearing inboxes, scheduling, drafting agendas, and preparing briefings. With customizable snapshots of the daily, weekly, and weekend schedule, users can stay organized and know exactly what they need to accomplish. Additionally, Sixty AI helps users stay on top of email communications by filtering out noise and prioritizing important messages.', NULL, '["Productivity Tools","Email Management","Life Management"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('8bcd540b-f720-41bd-a16a-0eafadf92691', '2024-04-12T15:29:21.737Z', 'Skelet AI', 'https://skelet.ai/', 'images/skelet-ai.webp', 'images/skelet-ai-icon.webp', '{"what":"Skelet AI is an all-in-one platform for AI-powered content generation in various formats, including text, images, and voice. It offers a range of AI-driven tools to simplify content creation and enhance user interactions.","who":"Skelet AI is designed for content creators, marketers, designers, and anyone looking to streamline their content creation process with AI-powered tools. It caters to individuals and businesses of all sizes.","best_features":["AI-driven content generation tool with diverse templates for compelling content creation","AI image generation tool for visually appealing graphics in 80+ languages","Text-to-Speech feature for natural and expressive vocal touch in 40+ languages"]}', 'Skelet AI is an all-in-one platform for AI-powered content generation, offering tools for text, image, and voice content creation. It simplifies the content creation process with diverse templates, AI image generation, and Text-to-Speech features. Users can choose from different subscription plans, including a free plan, and customize their experience based on their needs.', NULL, '["Content Creation","Image Generation","Text-to-Speech"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('49a054ff-2fef-44e7-aa9c-441ba37f215f', '2024-04-12T15:29:21.737Z', 'Skepticaltom', 'https://skepticaltom.com/', 'images/skepticaltom.webp', 'images/skepticaltom-icon.webp', '{"what":"Skeptical Tom is an AI tool designed to help users control their impulsive buying habits by encouraging thoughtful consideration before making purchases.","who":"Skeptical Tom is for individuals who struggle with impulsive shopping tendencies and want to make more mindful purchasing decisions.","best_features":["Utilizes artificial intelligence to intervene and discourage impulsive buying","Acts as a virtual cat companion to promote thoughtful consideration","Aims to positively impact users'' purchasing habits"],"pricing":[]}', 'Skeptical Tom is an AI tool that acts as a virtual cat companion to help users resist impulsive buying decisions. By utilizing artificial intelligence, Skeptical Tom intervenes when impulsive tendencies arise, encouraging users to reconsider their purchases. This tool aims to bring a positive impact to users'' purchasing habits by promoting thoughtful consideration and helping them avoid regrettable decisions.', NULL, '["AI Assistance","Personal Finance","Shopping Assistance"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('f7b68454-0729-4196-9c90-642c4d96604c', '2024-04-12T15:29:21.737Z', 'Sketch Ai Drawing To Art Maker', 'https://appintro.io/sketch-ai-drawing-to-art', 'images/sketch-ai-drawing-to-art-maker.webp', 'images/sketch-ai-drawing-to-art-maker-icon.webp', '{"what":"Sketch AI Drawing To Art Maker is an AI-powered tool that transforms sketches and ideas into digital artwork with just one click.","who":"This product is for artists, designers, content creators, and anyone looking to quickly digitize their sketches and unleash their creativity.","best_features":["Instantly turn sketches into digital art with one click","AI technology accurately interprets and transforms sketch ideas into various creative styles","Enhances efficiency of visual content creation and sparks creativity"],"pricing":[{"plan":"Basic","price":9.99,"billing_cycle":"monthly","original_price":14.99,"discount":"33% off","features":["Unlimited sketch to art conversions","Access to multiple creative styles","24/7 customer support"]},{"plan":"Pro","price":19.99,"billing_cycle":"monthly","original_price":29.99,"discount":"33% off","features":["Advanced AI technology for precise art generation","Priority customer support","Exclusive art styles"]}]}', 'Sketch AI Drawing To Art Maker is a revolutionary tool that uses AI technology to transform sketches into digital artwork in various creative styles. It enhances visual content creation efficiency and helps users unleash their creativity like never before. With just a few taps, users can see their ideas come to life on screen, making it a must-have for artists and designers.', NULL, '["Art Generation","Design","Creativity"]');
