INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('73742b68-fd2e-4792-be0f-01477580a922', '2024-04-12T15:29:31.542Z', 'Sketch2App', 'https://www.sketch2app.io/', 'images/sketch2app.webp', 'images/sketch2app-icon.webp', '{"what":"Sketch2App is a tool designed to automate mobile application development by generating boilerplate UI code from user-created sketches. It transforms hand-drawn sketches into functional code, synced with Visual Studio Code for seamless integration.","who":"Sketch2App is ideal for developers and designers looking to speed up the app development process and improve productivity. It caters to individuals and teams working on mobile applications who want to quickly prototype and design apps or components.","best_features":["Automated generation of code from hand-drawn sketches, speeding up development process","Seamless integration with Visual Studio Code for a smooth workflow","Ability to create new apps or components directly within the editor"],"pricing":[]}', 'Sketch2App is a revolutionary tool that automates mobile application development by transforming hand-drawn sketches into functional code. It streamlines the prototyping and design process, allowing users to create new apps or components directly within Visual Studio Code.', NULL, '["Design","Productivity"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('56de84cc-8406-434e-b8ea-ceb461c2e7e3', '2024-04-12T15:29:31.543Z', 'Sketch2Render', 'https://exp.graymatter.ai/', 'images/sketch2render.webp', 'images/sketch2render-icon.webp', '{"what":"Sketch2Render is an AI tool designed to convert hand-drawn sketches into realistic 3D models, providing users with a streamlined and automated approach to design visualization.","who":"Sketch2Render is for amateur and professional designers looking to easily translate their creative visions into three-dimensional forms, streamlining the design workflow and offering a quicker and more efficient way to generate 3D representations of hand-drawn sketches.","best_features":["Advanced AI capabilities for converting hand-drawn sketches into realistic 3D models","User-friendly interface catering to both amateur and professional designers","Streamlined design workflow for quicker and more efficient 3D model generation"],"pricing":[]}', 'Sketch2Render is an AI tool that converts hand-drawn sketches into realistic 3D models, allowing users to visualize and bring their design concepts to life. By leveraging advanced AI capabilities, this tool streamlines the design workflow and eliminates the need for complex manual modeling processes. With a user-friendly interface, Sketch2Render caters to both amateur and professional designers, offering a simplified and automated approach to transforming sketches into immersive 3D models.', NULL, '["3D Modeling","AI Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('49bef6ba-7668-489e-9e04-574f6a2a55a7', '2024-04-12T15:29:31.543Z', 'SketchGPT', 'https://www.sketchgpt.xyz/', 'images/sketchgpt.webp', 'images/sketchgpt-icon.webp', '{"what":"SketchGPT is a tool that utilizes AI to assist users in visualizing their product designs by enhancing sketches with shadows, depth, dimensions, and clean backgrounds. It was developed by Sean Liu.","who":"SketchGPT is designed for product designers, artists, entrepreneurs, and anyone looking to bring their product ideas to life through enhanced visualizations. It caters to individuals and businesses seeking to streamline their design process and improve the presentation of their products.","best_features":["AI-powered enhancement of product sketches for a more professional and realistic look.","Ability to view product designs under different themes for creative exploration.","User-friendly interface that simplifies the visualization process for all skill levels."]}', 'SketchGPT is a cutting-edge tool that leverages AI technology to transform basic product sketches into visually stunning representations. Users can upload their sketches and instantly see them enhanced with shadows, depth, and dimensions, all while maintaining a clean and professional background. This tool is ideal for individuals and businesses looking to elevate their product design process and create compelling visualizations with ease.', NULL, '["AI Tools","Art Generation","3D Modeling"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('bde0b4f0-066f-45e0-b3fd-f12a5595e700', '2024-04-12T15:29:31.544Z', 'SketchPro AI', 'https://sketchpro.ai/', 'images/sketchpro-ai.webp', 'images/sketchpro-ai-icon.webp', '{"what":"SketchPro AI is an AI tool designed for architects and interior designers to create sketches and renders. It offers two modes - preliminary sketch and detail sketch render, with high creative potential.","who":"SketchPro AI is for architects and interior designers looking to streamline their design process and enhance their creativity with AI assistance.","best_features":["Instant renders and AI-powered tools for fast sketching and 3D visualization","Flexible inputs allowing users to start with any sketch, elevation, 3D model, or image","Custom materials feature for changing materials instantly in designs"],"pricing":[]}', 'SketchPro AI is an AI design assistant specifically tailored for architects and interior designers. It offers features like instant renders, flexible inputs, and custom materials to enhance the design process. With SketchPro AI, users can visualize designs, iterate on concepts, and reimagine spaces with the power of AI at their fingertips.', NULL, '["Design","Architecture","Interior Design"]');
