INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('ef4aa44b-5bbf-49a2-83f4-1312107f85de', '2024-04-12T15:29:31.568Z', 'Songburst', 'https://www.songburst.ai/', 'images/songburst.webp', 'images/songburst-icon.webp', '{"what":"Songburst is an AI music generator that allows users to turn their words into music. It is a versatile tool designed for everyone, offering an easy-to-use interface and example prompts to inspire users in creating their own original songs.","who":"Songburst is for content creators, musicians, and anyone looking to easily create original music tracks for various online platforms such as videos, podcasts, and video games.","best_features":["AI music generation based on user descriptions","Unlimited downloads of generated songs in WAV or MP3 formats","Prompt enhancer feature for more descriptive prompts"]}', 'Songburst is an AI music generator that allows users to create original songs by describing the type of music they want, which is then generated by the AI algorithm. Users can use the generated music in videos, podcasts, and video games, with unlimited downloads available in WAV or MP3 formats. The tool also offers a prompt enhancer feature to make prompts more descriptive for better results. Songburst is available as a mobile app on iOS and Android platforms, with a web version in development.', NULL, '["AI Tools","Content Creation"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('c44e2808-db23-4072-acb4-69676232a638', '2024-04-12T15:29:31.569Z', 'Songhunt', 'https://www.mypart.com/songhunt/', 'images/songhunt.webp', 'images/songhunt-icon.webp', '{"what":"Songhunt is an AI-powered song search engine that revolutionizes music exploration by offering personalized search and playlist creation based on user preferences.","who":"Songhunt is for music enthusiasts looking to discover new songs, create customized playlists, and enhance their music exploration experience.","best_features":["AI-powered search using lyrical and musical filters for refined results","Playlist creation and export functionality for organizing music collection","Curated song selection based on user preferences for personalized music discovery"],"pricing":[]}', 'Songhunt is an AI-powered song search engine that offers users a platform to search for songs using lyrical and musical filters, create playlists, and discover new music based on their preferences. The tool employs artificial intelligence algorithms to enhance music exploration by providing personalized search results and curated song selections.', NULL, '["AI Tools","Search Engines"]');
