INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('8bd910b0-5dd7-4824-a19c-65640a142db6', '2024-04-12T15:29:31.569Z', 'Sonia Health', 'https://www.soniahealth.com/', 'images/sonia-health.webp', 'images/sonia-health-icon.webp', '{"what":"Sonia Health is an AI-powered cognitive behavioral therapy tool designed to make mental health care accessible and convenient through virtual therapy sessions.","who":"Sonia Health is for individuals seeking mental health support but facing barriers such as geographical distance, high costs, and social stigma. It is also beneficial for areas with low numbers of mental health professionals.","best_features":["Fully automated AI application providing therapy sessions via voice or text","Inclusivity in mental health care, overcoming barriers like geographical distance and high costs","24/7 accessibility, allowing users to conduct therapy sessions anytime and anywhere"]}', 'Sonia Health is an AI-powered cognitive behavioral therapy tool that offers virtual therapy sessions through voice or text, making mental health care accessible and convenient. It aims to overcome barriers like geographical distance, high costs, and social stigma associated with traditional therapy. Sonia provides therapy sessions round the clock, allowing users to engage in sessions from the comfort of their homes or on-the-go.', NULL, '["Mental Health","AI Tools","Therapy"]');
