INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('b94e22bb-489d-4a08-aee1-fdd58af8786c', '2024-04-12T15:29:31.586Z', 'StableBeluga2', 'https://huggingface.co/stabilityai/StableBeluga2', 'images/stablebeluga2.webp', 'images/stablebeluga2-icon.svg', '{"what":"StableBeluga2 is an auto-regressive language model developed by Stability AI and fine-tuned on the Llama2 70B dataset. It is designed to generate text based on user prompts.","who":"StableBeluga2 is for developers looking to utilize a powerful language model for natural language processing tasks such as text generation and conversational AI.","best_features":["Fine-tuned on Llama2 70B dataset for accurate text generation","Supports customization through parameters like top-p and top-k","Trained on an internal Orca-style dataset using mixed-precision training and optimized with AdamW"],"pricing":[{"plan":"","price":0,"billing_cycle":"","original_price":0,"discount":"","features":[]}]}', 'StableBeluga2 is an advanced language model that can generate text based on user prompts. It is trained on a large dataset and offers customization options for developers. The model is suitable for various natural language processing tasks.', NULL, '["Text Generation","Conversational AI"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('07491d6a-574c-4ce9-9e96-adcb4c7fe381', '2024-04-12T15:29:31.587Z', 'StableCascade', 'https://github.com/Stability-AI/StableCascade', 'images/stablecascade.webp', 'images/stablecascade-icon.svg', '{"what":"StableCascade is an AI model built upon the Wrstchen architecture, designed for generating images efficiently with a highly compressed latent space. It offers training & inference scripts, along with various models for different use cases.","who":"StableCascade is ideal for developers, data scientists, and AI enthusiasts looking to generate high-quality images with fast inference times and cost-effective training.","best_features":["Highly compressed latent space for faster inference and cheaper training","Efficient architecture for text-to-image generation","Provides multiple models for different stages of image generation"],"pricing":[]}', 'StableCascade is an AI model based on the Wrstchen architecture, offering efficient image generation with a highly compressed latent space. It provides training & inference scripts, along with various models for different stages of image generation.', NULL, '["Image Generation","AI Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('1b3ba9a8-c599-4e89-8615-46a3ac8f0a3f', '2024-04-12T15:29:31.587Z', 'StableCode', 'https://stability.ai/blog/stablecode-llm-generative-ai-coding', 'images/stablecode.webp', 'images/stablecode-icon.webp', '{"what":"StableCode is an LLM generative AI product for coding developed by Stability AI. It aims to assist programmers in their daily work and serve as a learning tool for new developers.","who":"StableCode is for programmers looking to enhance their coding efficiency, as well as new developers seeking to improve their skills. It is also suitable for beginners tackling more significant coding challenges.","best_features":["Three different models to enhance coding efficiency","Long-context window model for single and multiple-line autocomplete suggestions","Ability to handle 2-4 times more code at once compared to previous open models"],"pricing":[]}', 'StableCode is an LLM generative AI product for coding developed by Stability AI. It offers three different models to enhance coding efficiency, including a base model trained on various programming languages and an instruction model for complex programming tasks. The long-context window model allows for single and multiple-line autocomplete suggestions, making it an ideal assistant for reviewing or editing large amounts of code simultaneously. StableCode aims to make technology more accessible and provide fairer access to coding worldwide.', NULL, '["AI Tools","Education"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('91fe132b-ee2e-4475-a4f9-954e0f129610', '2024-04-12T15:29:31.587Z', 'StableLM Zephyr 3B', 'https://stability.ai/news/stablelm-zephyr-3b-stability-llm', 'images/stablelm-zephyr-3b.webp', 'images/stablelm-zephyr-3b-icon.webp', '{"what":"StableLM Zephyr 3B is a new chat model that represents the latest addition to the StableLM series of lightweight Large Language Models (LLMs) from Stability AI. It is designed to efficiently cater to a wide range of text generation needs without the requirement of high-end hardware.","who":"This product is ideal for individuals and businesses looking for a powerful and efficient language model that can handle various complex applications, from simple queries to complex instructional contexts, on edge devices.","best_features":["Efficiently handles various complex applications on edge devices","Preference-tuned for instruction following and Q&A-related tasks","Capable of standing up to larger models designed for similar use cases"],"pricing":[]}', 'StableLM Zephyr 3B is a 3 billion parameter chat model that efficiently caters to text generation needs on edge devices. It is 60% smaller than 7B models and adeptly handles instruction following, Q&A tasks, copywriting, summarization, and more. The model''s performance tests have shown its capability to compete with larger models in similar use cases.', NULL, '["Language Models","Text Generation","Edge Devices"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('8f1cf98f-fc79-49e8-aad6-78dbc48e49e8', '2024-04-12T15:29:31.587Z', 'Stablematic', 'https://stablematic.com/', 'images/stablematic.webp', 'images/stablematic-icon.webp', '{"what":"Stablematic is an AI tool that allows users to easily run stable diffusion machine learning models with the best hardware and without the need for setup.","who":"Stablematic is for individuals and businesses looking to efficiently run stable diffusion machine learning models with the best hardware and minimal setup requirements.","best_features":["Fast and easy setup with one-click deployment","Transparent runtime pricing based on actual usage with no hidden fees","Dedicated GPU playground instance for uninterrupted usage"],"pricing":[{"plan":"Public alpha","price":8,"billing_cycle":"per month","original_price":10,"discount":"Save 20% for a limited time","features":["Dedicated 1 on 1 feature support","Pre-installed models and support for custom models","API Access","Unlock future access bonuses and perks from Spellbook Labs"]}]}', 'Stablematic is an AI tool that simplifies the process of running stable diffusion machine learning models by providing a user-friendly web interface with the best hardware. It offers features such as text prompts to videos, image model training, and video animations generation. With transparent pricing based on runtime usage, users can access a dedicated GPU playground instance without cold start problems. Additionally, Stablematic provides API access and future access to bonus perks from Spellbook Labs.', NULL, '["Machine Learning","Image Generation","API Access"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('7e542012-b1de-4b38-b503-b1c0101706c7', '2024-04-12T15:29:31.587Z', 'StableStudio', 'https://stability.ai/blog/stablestudio-open-source-community-driven-future-dreamstudio-release', 'images/stablestudio.webp', 'images/stablestudio-icon.webp', '{"what":"StableStudio is an open-source text-to-image consumer application developed by Stability AI, aimed at facilitating community-driven development and local-first initiatives for generative AI.","who":"StableStudio is designed for developers looking to explore generative AI and its potential advancements, as well as for individuals interested in open-source development within the AI ecosystem.","best_features":["User-friendly platform for generative AI development","Community-driven collaboration and open-source initiatives","Extensible for external developers with bounties for improvements and new features"],"pricing":[]}', 'StableStudio, developed by Stability AI, is an open-source text-to-image consumer application that serves as a user-friendly platform for generative AI development. It promotes community-driven collaboration and local-first initiatives, offering extensibility for external developers and bounties for improvements and new features.', NULL, '["Generative AI","Open-Source Development","Community Collaboration"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('fa582e32-3a16-42ad-864b-2cce32f971c2', '2024-04-12T15:29:31.587Z', 'StackBear', 'https://stackbear.com', 'images/stackbear.webp', 'images/stackbear-icon.webp', '{"what":"Stackbear is an AI chatbot platform that allows users to create personalized chatbots trained on their own content. It offers natural language responses and seamless handover to human agents for more complex queries.","who":"Stackbear is designed for businesses looking to automate customer support, engage website visitors with personalized conversations, and capture leads for sales teams.","best_features":["Create a natural language bot that can understand complex queries and converse at a human level.","Knowledge base integration for users to ask questions and get answers from company content.","Human escalation feature to seamlessly transfer conversations to human agents when needed."]}', 'With Stackbear, creating personalized AI chatbots that are trained on your content is a quick and easy process. These bots are designed to automatically handle over 50% of customer queries, offering instant, human-like responses. For queries that require a more personal touch, the chatbot seamlessly transfers the conversation to your help desk. Setting up your AI chatbot with Stackbear is a matter of minutes, not months.', NULL, '["Chat Solutions","Customer Support","Sales Tools"]');
