', NULL, '["White Label","Digital Marketing","Automation"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('e88621f4-b1be-4741-8c53-5f330a2e90aa', '2024-04-12T15:29:31.588Z', 'Star Method Coach', 'https://starmethod.coach/', 'images/star-method-coach.webp', 'images/star-method-coach-icon.webp', '{"what":"Star Method Coach is an AI-based mock interview platform designed to help users practice their responses to interview questions using the STAR method.","who":"This product is for job seekers looking to improve their interview skills, particularly in behavioral interviews.","best_features":["Personalized learning with customized feedback on STAR stories","Real-world simulation of job interview scenarios","Audio-interactive learning for improved retention and engagement"],"pricing":[{"plan":"Standard Plan","price":29.99,"billing_cycle":"monthly","original_price":39.99,"discount":"25% off","features":["AI Coach guidance","Audio-based practice","Progress tracking"]}]}', 'Star Method Coach is an AI-powered mock interview platform that helps users practice and improve their interview skills using the STAR (Situation, Task, Action, Result) method. It offers personalized feedback, real-world simulation, and audio-interactive learning to enhance users'' preparedness for job interviews.', NULL, '["Job Searching","AI Solutions","Coaching"]');
