INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('2a4c7f4e-badf-44e0-8408-d9253ab42c40', '2024-04-12T15:29:31.588Z', 'Stardog', 'https://stardog.ai/', 'images/stardog.webp', 'images/stardog-icon.webp', '{"what":"Stardog Voicebox is a conversational data platform that enables users to interact with their data by asking questions and receiving accurate and explainable answers.","who":"This product is designed for knowledge workers in various industries such as financial services, manufacturing, and customer profiling.","best_features":["Generative AI for universal data access","Language Models for Learning (LLM) powered interface","Ability to query all data from on-premises, cloud, or both in a single chatbox"],"pricing":[{"plan":"Standard Plan","price":39,"billing_cycle":"per month","original_price":0,"discount":"Try it out for free","features":["Query all your data from everywhere in a single chatbox","Get accurate answers every time","Gain up to 95% efficiency"]}]}', 'Stardog Voicebox is a conversational data platform that allows users to interact with their data by asking questions and receiving accurate and explainable answers. It overcomes data challenges by connecting enterprise data based on business meaning, enabling knowledge workers to discover hidden insights without direct data access. The platform features Generative AI, Language Models for Learning, and offers a consolidated way of data handling and interaction.', NULL, '["Data Analysis","Enterprise","AI Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('3bda44b7-b845-44fd-87c3-bec61b3bf4bc', '2024-04-12T15:29:31.588Z', 'Starterbuild', 'https://starterbuild.com/business-idea-generator', 'images/starterbuild.webp', 'images/starterbuild-icon.webp', '{"what":"Starterbuild is an artificial intelligence-based Business Idea Generator tool developed to assist users in brainstorming and generating business ideas. It caters to a wide range of business types and revenue strategies, providing unique insights and market trends to guide aspiring entrepreneurs in forming and validating their business ideas.","who":"Starterbuild is designed for aspiring entrepreneurs who are looking for AI-based insights to help them generate and validate business ideas. It is also suitable for individuals interested in exploring various revenue strategies and market focuses to kickstart their entrepreneurial journey.","best_features":["AI-based business idea generation tailored to user inputs and market trends","Support for various revenue strategies including B2B, B2C, and government licensing","Access to generated ideas via email and social media platforms for easy sharing and collaboration"],"pricing":[]}', 'The Business Idea Generator by Starterbuild is an AI tool that helps users brainstorm and generate business ideas based on their interests and market trends. It supports different revenue strategies and market focuses, providing unique insights to guide aspiring entrepreneurs in forming and validating their business ideas.', NULL, '["AI Tools","Startup Resources","Entrepreneurship"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('86dffd69-d5bf-4dde-88dd-51ae42360e75', '2024-04-12T15:29:31.589Z', 'Startify', 'https://startify.softr.app/', 'images/startify.webp', 'images/startify-icon.webp', '{"what":"Startify is an AI tool designed to assist startup founders in overcoming challenges by providing comprehensive solutions. It offers a 7-day trial without the need for credit card information and utilizes an AI model to gather relevant information from research.","who":"Startify is for both novice and experienced entrepreneurs looking to streamline their startup journey, make informed decisions, and access expert startup insights instantly.","best_features":["Effortlessly find solutions to various problems","Mobile-friendly for access on any device","Instant access to expert startup insights"],"pricing":[]}', 'Startify is an AI tool designed to assist startup founders in overcoming challenges. It offers comprehensive solutions for both novice and experienced entrepreneurs, providing a 7-day trial without the need for credit card information. Startify utilizes an AI model to gather relevant information from research, ensuring users have access to reliable resources. It is mobile-friendly, offering a personal advisor at users'' fingertips to make informed decisions and streamline their startup journey.', NULL, '["AI Tools","Business Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('48b7676f-cf4c-41ab-8146-7c83a03e5143', '2024-04-12T15:29:31.589Z', 'Startilla', 'https://startilla.co/', 'images/startilla.webp', 'images/startilla-icon.webp', '{"what":"Startilla is an AI-powered business development platform designed to assist startups, product owners, business analysts, UX designers, and marketing agencies in formulating, iterating, and refining their digital business concepts.","who":"Startilla is for entrepreneurs seeking guidance in launching startup ideas, freelancers, business analysts, and product innovators shaping new business concepts.","best_features":["Automated creation of critical startup documents","Advanced algorithms for generating vision and mission statements","Insights into target audience behaviors, preferences, and needs"],"pricing":[]}', 'Startilla is an AI-powered business development platform that automates the creation of critical startup documents, generates vision and mission statements, provides insights into target audience behaviors, and offers SWOT analysis capabilities. It also assists in developing monetization strategies, creating marketing plans, and implementing sales strategies. Users can benefit from a free 30-minute business analysis session with startup experts. For agencies, Startilla offers client collaboration, project management features, and integration with various tools for an organized workflow.', NULL, '["Startup Resources","AI Tools","Machine Learning"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('eec9ad6a-06a6-4c4b-9104-505c523c9e5d', '2024-04-12T15:29:31.589Z', 'Startup Pitch Generator', 'https://tally.so/r/npeJgq', 'images/startup-pitch-generator.webp', 'images/startup-pitch-generator-icon.webp', '{"what":"The Startup Pitch Generator is an AI tool designed to help users create compelling elevator pitches and suggest business models for their projects.","who":"This product is ideal for entrepreneurs, startup founders, and anyone looking to refine their pitch and business model for their new venture.","best_features":["Accurately addresses the problem a project solves and the lack of relevant solutions in the market","Provides a description of the need and suggests a business model","Allows users to describe one central valuable process and the flow of potential user actions"],"pricing":[]}', 'The Startup Pitch Generator is an AI tool that assists users in crafting effective elevator pitches and business models. It helps users clearly define the problem their project solves, the existing market gaps, and guides them through outlining key processes and user actions. This tool does not require a startup app and allows users to submit their pitch anonymously.', NULL, '["AI Assistance","Startup Resources"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('216ce63d-0a26-4bd8-bf8b-75670cb4444d', '2024-04-12T15:29:31.589Z', 'Startup Studio', 'https://startupstudio-ai.com/', 'images/startup-studio.webp', 'images/startup-studio-icon.webp', '{"what":"Startup Studio is an AI tool designed to assist startups in various aspects of their business journey, from idea validation to brand building, customer profiling, marketing plans, and investment decisions.","who":"This product is ideal for aspiring entrepreneurs and startup founders who are looking to leverage AI technology to streamline and enhance their business operations.","best_features":["AI-powered idea validation for successful launch","Branding assistance to make businesses stand out","Personalized marketing plan suggestions for effective advertising"],"pricing":[]}', 'Startup Studio is an AI tool that simplifies the entrepreneurial journey for startups by providing assistance in idea assessment, brand building, customer profiling, marketing plans, and investment decisions. It uses AI technology to help startups establish and scale up effectively, making the process of starting and growing a business more efficient and successful.', NULL, '["AI Tools","Business Tools"]');
