INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('b50a86d4-9359-4676-b6a9-bef2f8bd9d54', '2024-04-12T15:29:31.590Z', 'Starving Robots', 'https://www.starvingrobots.com/', 'images/starving-robots.webp', 'images/starving-robots-icon.webp', '{"what":"Starving Robots is an advanced platform that allows users to upscale and print their AI-generated artwork onto various high-quality mediums.","who":"This product is for AI art enthusiasts looking to showcase their creative AI-powered creations through high-quality prints and merchandise.","best_features":["Cutting-edge upscaling technology for enhanced print quality","Premium museum-grade paper for superior print results","Custom art prints guided by user''s words and images"],"pricing":[]}', 'Starving Robots is a next-gen platform that empowers users to transform their AI art creations into tangible masterpieces through high-quality prints on posters, canvases, and stickers. By harnessing the power of artificial intelligence, users can bring their digital artwork to life with stunning HD quality prints. The platform offers a range of mediums to choose from and encourages users to subscribe to their newsletter for updates on AI art advancements.', NULL, '["Art Generation","Printing","Merchandise"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('1f4ac73a-7955-4707-aeb8-ca62245ed0a6', '2024-04-12T15:29:31.590Z', 'Staypia', 'https://www.staypia.com/', 'images/staypia.webp', 'images/staypia-icon.webp', '{"what":"Staypia is an AI-powered hotel reservation system that offers discounts on over 3 million hotels worldwide. Users can search for hotels by city and date, and benefit from member-exclusive pricing.","who":"Staypia is for travelers looking to save money on hotel reservations and access member-exclusive discounts on a wide range of hotels worldwide.","best_features":["AI-powered hotel reservation system","Discounts on over 3 million hotels worldwide","Member-exclusive pricing and additional discounts"],"pricing":[{"plan":"Standard","price":0,"billing_cycle":"Free","original_price":0,"discount":"N/A","features":["AI-powered hotel search","Member-exclusive pricing","Customer service support"]}]}', 'Staypia is an AI-powered hotel reservation system that offers discounts on over 3 million hotels worldwide. Users can search for hotels by city and date, and benefit from member-exclusive pricing. The tool compares prices across top travel websites and provides additional discounts to its members. Staypia also offers customer service support to its users. Prices on Staypia may differ from the prices advertised on other booking sites due to factors such as duration of stay, tax inclusion, and regional policies.', NULL, '["AI Services","Artificial Intelligence"]');
